import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getUserThreads } from "../firebase/messages.js";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function MessagingCenterAdmin() {
  const { user, role } = useAuth();
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  const isAdmin = role === "admin";

  useEffect(() => {
    if (!user || !isAdmin) return;

    (async () => {
      const data = await getUserThreads(user.uid); // use admin UID
      setThreads(data);
      setLoading(false);
    })();
  }, [user, isAdmin]);

  return (
    <div className="page fade-in">
      <img
        src="/logo.png"
        alt="HI AWTO"
        style={{
          width: "120px",
          display: "block",
          margin: "0 auto 20px",
        }}
      />

      <h1 className="page-title" style={{ textAlign: "center" }}>
        Admin Messaging Center
      </h1>

      {loading && <div>Loading threads...</div>}

      {!loading && threads.length === 0 && (
        <div className="card" style={{ textAlign: "center" }}>
          No message threads found.
        </div>
      )}

      <div style={{ display: "grid", gap: "16px" }}>
        {threads.map((t) => (
          <Link
            to={`/thread/${t.id}`}
            key={t.id}
            className="card hover-glow"
            style={{
              padding: "16px",
              display: "block",
              textDecoration: "none",
            }}
          >
            <div style={{ fontSize: "18px", fontWeight: 600 }}>
              {t.otherUser?.displayName || "User"}
            </div>

            <div style={{ marginTop: "6px", color: "#555", fontSize: "14px" }}>
              {t.lastMessage || "No messages yet"}
            </div>

            <div style={{ marginTop: "6px", fontSize: "12px", color: "#888" }}>
              {new Date(t.updatedAt).toLocaleString()}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
