import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllThreads } from "../firebase/messages.js";

export default function MessagingCenterAdmin() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      const data = await getAllThreads();
      setThreads(data);
      setLoading(false);
    })();
  }, []);

  return (
    <div className="page fade-in">
      <img
        src="/logo.png"
        alt="HI AWTO"
        style={{
          width: "130px",
          display: "block",
          margin: "0 auto 20px",
          filter: "drop-shadow(0 0 10px var(--blue))"
        }}
      />

      <h1 className="page-title" style={{ textAlign: "center" }}>
        Admin Messaging Center
      </h1>

      {loading && <div>Loading threads...</div>}

      {!loading && threads.length === 0 && (
        <div className="card" style={{ textAlign: "center" }}>
          No message threads found.
        </div>
      )}

      {/* Thread List */}
      <div style={{ display: "grid", gap: "16px" }}>
        {threads.map((t) => (
          <Link
            to={`/thread/${t.id}`}
            key={t.id}
            className="card hover-glow"
            style={{
              padding: "16px",
              display: "block",
              textDecoration: "none"
            }}
          >
            <div style={{ fontSize: "18px", fontWeight: 600, color: "var(--blue)" }}>
              {t.participants?.join(", ") || "Users"}
            </div>

            <div style={{ fontSize: "14px", color: "#bbb", marginTop: "6px" }}>
              {t.lastMessage || "No messages yet"}
            </div>

            <div style={{ fontSize: "12px", marginTop: "6px", color: "#777" }}>
              {new Date(t.updatedAt).toLocaleString()}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
