// src/pages/CreateListing.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { createListing } from "../firebase/listings.js";
import { uploadListingImages } from "../firebase/uploadManager.js";

export default function CreateListing() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    title: "",
    address: "",
    price: "",
    downPayment: "",
    description: "",
    featured: false,
  });

  const [imageFiles, setImageFiles] = useState([]); // File[]
  const [imagePreviews, setImagePreviews] = useState([]); // object URLs
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({
      ...f,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleImagesChange = (e) => {
    const files = Array.from(e.target.files || []);
    setImageFiles(files);
    setImagePreviews(files.map((file) => URL.createObjectURL(file)));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSubmitting(true);

    try {
      // 1) Upload images if any
      let imageUrls = [];
      if (imageFiles.length > 0) {
        imageUrls = await uploadListingImages(imageFiles);
      }

      // 2) Create listing in Firestore
      const payload = {
        title: form.title.trim(),
        address: form.address.trim(),
        price: form.price ? Number(form.price) : null,
        downPayment: form.downPayment ? Number(form.downPayment) : null,
        description: form.description.trim(),
        featured: !!form.featured,
        imageUrls, // new field
      };

      await createListing(payload);
      navigate("/my-listings");
    } catch (err) {
      console.error(err);
      setError(err?.message || "Failed to create listing.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto px-4 py-10">
      <h1 className="text-2xl md:text-3xl font-bold text-slate-800 mb-6">
        Create a New Listing
      </h1>

      {error && (
        <div className="mb-4 rounded bg-red-50 text-red-700 px-3 py-2 text-sm">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 rounded-xl shadow">
        {/* Title */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Title
          </label>
          <input
            type="text"
            name="title"
            value={form.title}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2 text-sm"
            required
          />
        </div>

        {/* Address */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Address
          </label>
          <input
            type="text"
            name="address"
            value={form.address}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2 text-sm"
            required
          />
        </div>

        {/* Price + Down payment */}
        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Total Price
            </label>
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              className="w-full border rounded-lg px-3 py-2 text-sm"
              min="0"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Down Payment
            </label>
            <input
              type="number"
              name="downPayment"
              value={form.downPayment}
              onChange={handleChange}
              className="w-full border rounded-lg px-3 py-2 text-sm"
              min="0"
              required
            />
          </div>
        </div>

        {/* Description */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Description
          </label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            rows={4}
            className="w-full border rounded-lg px-3 py-2 text-sm"
            placeholder="Describe the property, neighborhood, and lease-to-own terms."
          />
        </div>

        {/* Featured toggle */}
        <div className="flex items-center gap-2">
          <input
            id="featured"
            type="checkbox"
            name="featured"
            checked={form.featured}
            onChange={handleChange}
            className="h-4 w-4"
          />
          <label htmlFor="featured" className="text-sm text-gray-700">
            Mark as featured
          </label>
        </div>

        {/* Images */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Listing Images
          </label>
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={handleImagesChange}
            className="block text-sm text-gray-600"
          />
          <p className="text-xs text-gray-400 mt-1">
            You can upload several images. The first one will be used as the main photo.
          </p>

          {imagePreviews.length > 0 && (
            <div className="mt-3 grid grid-cols-3 md:grid-cols-4 gap-2">
              {imagePreviews.map((src, idx) => (
                <div key={idx} className="relative h-20 rounded overflow-hidden border">
                  <img
                    src={src}
                    alt={`Preview ${idx + 1}`}
                    className="w-full h-full object-cover"
                  />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Submit */}
        <div className="pt-2">
          <button
            type="submit"
            disabled={submitting}
            className="inline-flex items-center justify-center px-6 py-2 rounded-full bg-blue-600 text-white font-semibold hover:bg-blue-700 disabled:opacity-60"
          >
            {submitting ? "Saving listing..." : "Create Listing"}
          </button>
        </div>
      </form>
    </div>
  );
}
