// src/pages/MyListings.jsx
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getListingsForUser, deleteListing } from "../firebase/listings.js";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function MyListings() {
  const { user } = useAuth();
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) return;

    (async () => {
      setLoading(true);
      try {
        const data = await getListingsForUser(user.uid);
        setListings(data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load your listings.");
      } finally {
        setLoading(false);
      }
    })();
  }, [user]);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this listing?")) return;

    try {
      await deleteListing(id);
      setListings((prev) => prev.filter((l) => l.id !== id));
    } catch (err) {
      console.error(err);
      alert("Failed to delete listing.");
    }
  };

  if (!user) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-600">You must be logged in to view your listings.</p>
      </div>
    );
  }

  return (
    <div className="max-w-5xl mx-auto px-4 py-10">
      <h1 className="text-2xl md:text-3xl font-bold text-slate-800 mb-6">
        My Listings
      </h1>

      {loading && <p className="text-gray-500">Loading your listingsâ€¦</p>}
      {error && <p className="text-red-600">{error}</p>}

      {!loading && listings.length === 0 && (
        <p className="text-gray-600">You have no listings yet.</p>
      )}

      <div className="grid gap-4 md:grid-cols-2">
        {listings.map((l) => (
          <div
            key={l.id}
            className="bg-white p-4 rounded-xl shadow hover:shadow-md transition"
          >
            <Link to={`/listing/${l.id}`}>
              <h2 className="font-semibold text-lg">{l.title}</h2>
              <p className="text-gray-600 text-sm">{l.address}</p>
              <p className="mt-1 text-blue-600 font-medium">
                ${l.price?.toLocaleString() || l.price}
              </p>
            </Link>

            <div className="flex gap-2 mt-3">
              <Link
                to={`/listing/${l.id}`}
                className="text-blue-600 text-sm hover:underline"
              >
                View
              </Link>

              <button
                onClick={() => handleDelete(l.id)}
                className="text-red-600 text-sm hover:underline"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
