import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { getListing } from "../firebase/listings.js";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function ListingDetails() {
  const { id } = useParams();
  const { user } = useAuth();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      const data = await getListing(id);
      setListing(data);
      setLoading(false);
    })();
  }, [id]);

  if (loading) {
    return <div className="page">Loading listing...</div>;
  }

  if (!listing) {
    return <div className="page">Listing not found.</div>;
  }

  return (
    <div className="page fade-in">
      <h1 className="page-title" style={{ textAlign: "center" }}>
        {listing.title || "Listing"}
      </h1>

      <div className="card">
        <p>
          <strong>Address:</strong> {listing.address}
        </p>
        <p>
          <strong>Price:</strong>{" "}
          ${listing.price?.toLocaleString?.() || listing.price}
        </p>
        <p>
          <strong>Down Payment:</strong>{" "}
          ${listing.downPayment?.toLocaleString?.() || listing.downPayment}
        </p>
        <p>
          <strong>Lease Terms:</strong> {listing.leaseTerms || "—"}
        </p>
        <p style={{ marginTop: "10px" }}>
          <strong>Description:</strong>{" "}
          {listing.description || "No description."}
        </p>
      </div>

      {user && user.uid !== listing.ownerId && (
        <Link
          to="/messages"
          className="btn btn-primary"
          style={{ marginTop: "16px", display: "inline-flex" }}
        >
          Contact Seller
        </Link>
      )}
    </div>
  );
}
