import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import { createListing } from "../firebase/listings.js";
import { uploadMultipleFiles } from "../firebase/uploadManager.js";

export default function CreateListing() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",
    address: "",
    price: "",
    downPayment: "",
    leaseTerms: "",
    description: "",
  });
  const [files, setFiles] = useState([]);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  if (!user) {
    return (
      <div className="page">
        <div className="card">You must be logged in to create a listing.</div>
      </div>
    );
  }

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files || []));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError("");

    try {
      let imageUrls = [];
      if (files.length > 0) {
        imageUrls = await uploadMultipleFiles(files, `listings/${user.uid}`);
      }

      const payload = {
        ...form,
        price: Number(form.price) || 0,
        downPayment: Number(form.downPayment) || 0,
        ownerId: user.uid,
        imageUrls,
      };

      const id = await createListing(payload);
      navigate(`/listing/${id}`);
    } catch (err) {
      console.error(err);
      setError(err.message || "Failed to create listing");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="page fade-in">
      <h1 className="page-title">Create Listing</h1>

      {error && <div className="auth-error">{error}</div>}

      <form
        onSubmit={handleSubmit}
        className="card"
        style={{ display: "grid", gap: "12px" }}
      >
        <input
          className="input"
          name="title"
          placeholder="Title"
          value={form.title}
          onChange={handleChange}
          required
        />
        <input
          className="input"
          name="address"
          placeholder="Address"
          value={form.address}
          onChange={handleChange}
          required
        />
        <input
          className="input"
          name="price"
          type="number"
          placeholder="Total Price"
          value={form.price}
          onChange={handleChange}
          required
        />
        <input
          className="input"
          name="downPayment"
          type="number"
          placeholder="Down Payment"
          value={form.downPayment}
          onChange={handleChange}
          required
        />
        <input
          className="input"
          name="leaseTerms"
          placeholder="Lease Terms (e.g. 3 years, option to buy)"
          value={form.leaseTerms}
          onChange={handleChange}
        />
        <textarea
          className="input"
          style={{ minHeight: "90px" }}
          name="description"
          placeholder="Describe the property and lease-to-own terms"
          value={form.description}
          onChange={handleChange}
        />

        <div>
          <label
            style={{
              fontSize: "0.9rem",
              marginBottom: "4px",
              display: "block",
            }}
          >
            Photos
          </label>
          <input type="file" multiple onChange={handleFileChange} />
        </div>

        <button
          type="submit"
          disabled={saving}
          className="btn btn-primary"
          style={{ marginTop: "8px" }}
        >
          {saving ? "Creating..." : "Create Listing"}
        </button>
      </form>
    </div>
  );
}
