// src/pages/ViewThread.jsx
import React, { useEffect, useState, useRef, useCallback } from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  sendMessage,
  setThreadTyping,
  markThreadMessagesRead,
} from "../firebase/messages.js";
import { uploadChatAttachmentsWithProgress } from "../firebase/uploadManager.js";
import { db } from "../firebase/config.js";
import {
  doc,
  onSnapshot,
  collection,
  query,
  orderBy,
} from "firebase/firestore";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  const [newMessage, setNewMessage] = useState("");

  // Attachments + previews
  const [attachmentFiles, setAttachmentFiles] = useState([]);
  const [attachmentPreviews, setAttachmentPreviews] = useState([]);
  const [uploadProgress, setUploadProgress] = useState([]);
  const [uploadError, setUploadError] = useState("");
  const fileInputRef = useRef(null);

  // Drag + drop
  const [isDragging, setIsDragging] = useState(false);
  const dragCounterRef = useRef(0);

  // Typing
  const [typingUsers, setTypingUsers] = useState([]);
  const typingTimeoutRef = useRef(null);
  const typingActiveRef = useRef(false);

  // Lightbox
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  // Allowed file types
  const allowedTypes = [
    "image/jpeg", "image/png", "image/webp", "image/gif",
    "video/mp4", "video/quicktime",
    "application/pdf",
    "application/msword",
    "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
  ];

  const MAX_FILE_MB = 25;
  const MAX_TOTAL_MB = 50;
  const bytesToMB = (b) => b / (1024 * 1024);

  // Load thread
  useEffect(() => {
    if (!threadId) return;
    (async () => {
      const t = await getThread(threadId);
      setThread(t || null);
      setLoading(false);
    })();
  }, [threadId]);

  // Live messages
  useEffect(() => {
    if (!threadId) return;

    const msgsRef = collection(db, `threads/${threadId}/messages`);
    const q = query(msgsRef, orderBy("createdAt", "asc"));

    const unsub = onSnapshot(q, (snap) => {
      setMessages(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });

    return unsub;
  }, [threadId]);

  // Live typing state
  useEffect(() => {
    if (!threadId || !user) return;

    const ref = doc(db, "threads", threadId);
    const unsub = onSnapshot(ref, (snap) => {
      if (!snap.exists()) return;

      const data = snap.data();
      setThread({ id: snap.id, ...data });

      const typingMap = data.typing || {};
      const others = Object.entries(typingMap)
        .filter(([uid, isOn]) => uid !== user.uid && isOn)
        .map(([uid]) => uid);

      setTypingUsers(others);
    });

    return unsub;
  }, [threadId, user]);

  // Mark messages read
  useEffect(() => {
    if (!user || !threadId || messages.length === 0) return;
    markThreadMessagesRead(threadId, user.uid);
  }, [messages, threadId, user]);

  // ----------
  // FILE VALIDATION + PREVIEW
  // ----------
  function processFiles(files) {
    let valid = [];
    let previews = [];
    let errors = [];

    files.forEach((file) => {
      const sizeMb = bytesToMB(file.size);

      if (!allowedTypes.includes(file.type)) {
        errors.push(`‚ùå Unsupported: ${file.name}`);
        return;
      }
      if (sizeMb > MAX_FILE_MB) {
        errors.push(`‚ùå Too big: ${file.name}`);
        return;
      }

      valid.push(file);
      previews.push({
        name: file.name,
        type: file.type,
        url: URL.createObjectURL(file),
        size: file.size,
      });
    });

    const totalMb = valid.reduce((s, f) => s + bytesToMB(f.size), 0);
    if (totalMb > MAX_TOTAL_MB) {
      errors.push(`‚ùå Total exceeds ${MAX_TOTAL_MB}MB`);
      valid = [];
      previews.forEach((p) => URL.revokeObjectURL(p.url));
      previews = [];
    }

    setUploadError(errors.join("\n"));
    setAttachmentFiles(valid);
    setAttachmentPreviews(previews);
    setUploadProgress(valid.map(() => 0));

    if (errors.length > 0 && fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  }

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files || []);
    processFiles(files);
  };

  // Clear attachments
  const clearAttachments = () => {
    attachmentPreviews.forEach((p) => URL.revokeObjectURL(p.url));
    setAttachmentFiles([]);
    setAttachmentPreviews([]);
    setUploadProgress([]);
    setUploadError("");
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  // ----------
  // DRAG & DROP
  // ----------
  const handleDragEnter = (e) => {
    e.preventDefault();
    dragCounterRef.current++;
    setIsDragging(true);
  };
  const handleDragLeave = (e) => {
    e.preventDefault();
    dragCounterRef.current--;
    if (dragCounterRef.current <= 0) setIsDragging(false);
  };
  const handleDragOver = (e) => e.preventDefault();
  const handleDrop = (e) => {
    e.preventDefault();
    dragCounterRef.current = 0;
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files || []);
    processFiles(files);
  };

  // ----------
  // SEND MESSAGE
  // ----------
  const handleSend = async (e) => {
    e.preventDefault();

    const hasText = newMessage.trim().length > 0;
    const hasFiles = attachmentFiles.length > 0;

    if (!hasText && !hasFiles) {
      setUploadError("Message or attachment required");
      return;
    }

    setSending(true);

    try {
      let attachments = [];

      if (attachmentFiles.length > 0) {
        attachments = await uploadChatAttachmentsWithProgress(
          threadId,
          attachmentFiles,
          (index, percent) => {
            setUploadProgress((prev) => {
              const copy = [...prev];
              copy[index] = percent;
              return copy;
            });
          }
        );
      }

      await sendMessage(threadId, {
        text: newMessage.trim(),
        senderId: user.uid,
        senderName: user.displayName || user.email || "User",
        attachments,
        readBy: [user.uid],
      });

      setNewMessage("");
      clearAttachments();
    } catch (err) {
      console.error(err);
      setUploadError("Failed to send message");
    }

    setSending(false);
    setThreadTyping(threadId, user.uid, false);
  };

  // ----------
  // TYPING INDICATOR
  // ----------
  const handleInputChange = (e) => {
    const v = e.target.value;
    setNewMessage(v);

    if (!typingActiveRef.current) {
      typingActiveRef.current = true;
      setThreadTyping(threadId, user.uid, true);
    }

    if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);

    typingTimeoutRef.current = setTimeout(() => {
      typingActiveRef.current = false;
      setThreadTyping(threadId, user.uid, false);
      typingTimeoutRef.current = null;
    }, 1800);
  };

  const handleBlur = () => {
    typingActiveRef.current = false;
    setThreadTyping(threadId, user.uid, false);
  };

  // ----------
  // BUILD IMAGE LIST for lightbox navigation
  // ----------
  const imageAttachments = [];
  messages.forEach((m) => {
    (m.attachments || []).forEach((att) => {
      if ((att.contentType || "").startsWith("image/")) {
        imageAttachments.push(att);
      }
    });
  });

  // ----------
  // LIGHTBOX KEYBOARD SUPPORT (ESC / LEFT / RIGHT)
  // ----------
  const handleKeyDown = useCallback(
    (e) => {
      if (!lightboxOpen) return;

      if (e.key === "Escape") {
        setLightboxOpen(false);
      } else if (e.key === "ArrowRight") {
        setLightboxIndex((idx) => (idx + 1) % imageAttachments.length);
        setZoom(1);
      } else if (e.key === "ArrowLeft") {
        setLightboxIndex((idx) =>
          (idx - 1 + imageAttachments.length) % imageAttachments.length
        );
        setZoom(1);
      }
    },
    [lightboxOpen, imageAttachments.length]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // ----------
  // RENDER LOADING STATES
  // ----------
  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Loading‚Ä¶</p>
      </div>
    );
  }

  if (!thread) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Thread not found.</p>
        <Link to="/messages" className="text-blue-600 text-sm underline">
          ‚Üê Back
        </Link>
      </div>
    );
  }

  const otherParticipants =
    (thread.participants || []).filter((uid) => uid !== user?.uid) || [];

  return (
    <div className="max-w-3xl mx-auto px-4 py-10 flex flex-col gap-4">
      {/* HEADER */}
      <div className="flex justify-between items-center">
        <Link to="/messages" className="text-blue-600 text-xs">
          ‚Üê Back
        </Link>
        <h1 className="text-xl font-semibold">Conversation</h1>
      </div>

      {/* MESSAGES */}
      <div className="bg-white rounded-xl shadow p-4 max-h-[60vh] overflow-y-auto space-y-4">
        {messages.map((m) => {
          const isMine = m.senderId === user.uid;
          const attachments = m.attachments || [];

          const otherRead =
            isMine &&
            otherParticipants.length > 0 &&
            otherParticipants.every((uid) =>
              (m.readBy || []).includes(uid)
            );

          return (
            <div
              key={m.id}
              className={`flex flex-col ${
                isMine ? "items-end" : "items-start"
              }`}
            >
              <div
                className={`
                  max-w-[75%] rounded-2xl px-3 py-2 text-sm 
                  ${
                    isMine
                      ? "bg-blue-600 text-white rounded-br-sm"
                      : "bg-gray-100 text-slate-800 rounded-bl-sm"
                  }
                `}
              >
                {!isMine && m.senderName && (
                  <div className="text-[11px] font-semibold opacity-70 mb-1">
                    {m.senderName}
                  </div>
                )}

                {m.text && <div className="mb-1">{m.text}</div>}

                {attachments.length > 0 && (
                  <div className="space-y-2">
                    {attachments.map((att, idx) => {
                      const type = att.contentType || "";

                      // IMAGE ‚Üí click to open lightbox
                      if (type.startsWith("image/")) {
                        const imgIndex = imageAttachments.findIndex(
                          (x) => x.url === att.url
                        );
                        return (
                          <button
                            key={idx}
                            type="button"
                            className="block rounded-lg overflow-hidden focus:outline-none"
                            onClick={() => {
                              if (imgIndex >= 0) {
                                setLightboxIndex(imgIndex);
                                setZoom(1);
                                setLightboxOpen(true);
                              }
                            }}
                          >
                            <img
                              src={att.url}
                              alt={att.name}
                              className="max-h-40 rounded-lg object-cover"
                            />
                          </button>
                        );
                      }

                      // VIDEO
                      if (type.startsWith("video/")) {
                        return (
                          <video
                            key={idx}
                            src={att.url}
                            controls
                            className="max-h-40 rounded-lg"
                          />
                        );
                      }

                      // FILE LINK
                      return (
                        <a
                          key={idx}
                          href={att.url}
                          target="_blank"
                          rel="noreferrer"
                          className="underline text-xs flex items-center gap-1"
                        >
                          üìé {att.name}
                        </a>
                      );
                    })}
                  </div>
                )}
              </div>

              {otherRead && (
                <div className="text-[11px] text-gray-400 mt-1">Seen</div>
              )}
            </div>
          );
        })}

        {/* Typing */}
        {typingUsers.length > 0 && (
          <p className="text-xs text-gray-500 italic">Someone is typing‚Ä¶</p>
        )}
      </div>

      {/* ERROR */}
      {uploadError && (
        <div className="bg-red-50 text-red-600 text-xs rounded-lg px-3 py-2 whitespace-pre-line">
          {uploadError}
        </div>
      )}

      {/* PREVIEWS WITH PROGRESS */}
      {attachmentPreviews.length > 0 && (
        <div className="bg-white rounded-xl shadow p-3 flex flex-col gap-3">
          {attachmentPreviews.map((p, idx) => (
            <div
              key={idx}
              className="flex items-center gap-2 border rounded-lg px-2 py-1 text-xs"
            >
              {p.type.startsWith("image/") && (
                <img
                  src={p.url}
                  alt={p.name}
                  className="h-10 w-10 rounded object-cover"
                />
              )}
              {p.type.startsWith("video/") && <span>üé¨</span>}
              {!p.type.startsWith("image/") &&
                !p.type.startsWith("video/") && <span>üìé</span>}

              <span className="max-w-[150px] truncate">{p.name}</span>

              <div className="flex-1 h-2 bg-gray-200 rounded-full ml-2 overflow-hidden">
                <div
                  className="h-full bg-blue-600 transition-all"
                  style={{ width: `${uploadProgress[idx]}%` }}
                />
              </div>

              <span className="text-[10px] text-gray-500 w-10 text-right">
                {uploadProgress[idx]}%
              </span>
            </div>
          ))}

          <button
            onClick={clearAttachments}
            className="text-red-600 text-xs underline self-end"
          >
            Clear
          </button>
        </div>
      )}

      {/* COMPOSER */}
      <form
        onSubmit={handleSend}
        className={`bg-white rounded-xl shadow p-3 flex flex-col gap-3 border-2 ${
          isDragging ? "border-blue-400 bg-blue-50/40 border-dashed" : "border-transparent"
        }`}
        onDragEnter={handleDragEnter}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <div className="flex items-center gap-3">
          <label className="cursor-pointer text-sm text-blue-600 hover:underline">
            + Add files
            <input
              ref={fileInputRef}
              type="file"
              multiple
              className="hidden"
              onChange={handleFileChange}
            />
          </label>

          <span className="text-xs text-gray-400">
            or drag & drop files here
          </span>
        </div>

        <div className="flex items-end gap-3">
          <textarea
            rows={2}
            value={newMessage}
            onChange={handleInputChange}
            onBlur={handleBlur}
            placeholder={
              isDragging ? "Drop files‚Ä¶" : "Type your message‚Ä¶"
            }
            className="flex-1 border rounded-lg px-3 py-2 text-sm resize-none focus:outline-none focus:ring-blue-500/40 focus:border-blue-500"
          />

          <button
            type="submit"
            disabled={
              sending ||
              (!newMessage.trim() && attachmentFiles.length === 0)
            }
            className="px-5 py-2 bg-blue-600 text-white rounded-full text-sm hover:bg-blue-700 disabled:opacity-50"
          >
            {sending ? "Sending‚Ä¶" : "Send"}
          </button>
        </div>
      </form>

      {/* LIGHTBOX */}
      {lightboxOpen && imageAttachments.length > 0 && (
        <div
          className="fixed inset-0 bg-black/80 flex items-center justify-center z-50"
          onClick={() => setLightboxOpen(false)}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Top bar */}
            <div className="flex items-center justify-between mb-3 text-white text-sm">
              <div>
                {imageAttachments[lightboxIndex]?.name}{" "}
                <span className="opacity-60">
                  ({lightboxIndex + 1}/{imageAttachments.length})
                </span>
              </div>
              <button
                onClick={() => setLightboxOpen(false)}
                className="px-3 py-1 border border-white/40 rounded-full"
              >
                Close ‚úï
              </button>
            </div>

            {/* Image */}
            <div className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]">
              <img
                src={imageAttachments[lightboxIndex].url}
                alt=""
                style={{ transform: `scale(${zoom})` }}
                className="max-h-[80vh] max-w-full transition-transform"
              />
            </div>

            {/* Controls */}
            <div className="flex items-center justify-between mt-3 text-white text-sm">
              <div className="flex gap-2">
                <button
                  onClick={() =>
                    setLightboxIndex(
                      (i) =>
                        (i - 1 + imageAttachments.length) %
                        imageAttachments.length
                    )
                  }
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ‚Üê Prev
                </button>
                <button
                  onClick={() =>
                    setLightboxIndex(
                      (i) => (i + 1) % imageAttachments.length
                    )
                  }
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Next ‚Üí
                </button>
              </div>

              <div className="flex gap-2 items-center">
                <button
                  onClick={() => setZoom((z) => Math.max(z - 0.25, 0.5))}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ‚àí
                </button>
                <span>{(zoom * 100).toFixed(0)}%</span>
                <button
                  onClick={() => setZoom((z) => Math.min(z + 0.25, 3))}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  +
                </button>
                <button
                  onClick={() => setZoom(1)}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
