// src/pages/ListingDetails.jsx
import React, {
  useEffect,
  useState,
  useCallback,
  useRef
} from "react";
import { useParams, Link } from "react-router-dom";
import { getListingById } from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

export default function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  // Swipe
  const touchStartXRef = useRef(null);
  const touchEndXRef = useRef(null);
  const SWIPE_THRESHOLD = 50;

  // Double-tap / double-click
  const lastTapRef = useRef(0);
  const DOUBLE_TAP_THRESHOLD = 250; // ms

  useEffect(() => {
    (async () => {
      const data = await getListingById(id);
      setListing(data || null);
      setLoading(false);
    })();
  }, [id]);

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading…</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Listing not found.</p>
        <Link to="/" className="text-blue-600 hover:underline">
          ← Back
        </Link>
      </div>
    );
  }

  const images = listing.imageUrls?.length
    ? listing.imageUrls
    : [FALLBACK_IMG];

  const openLightbox = (i) => {
    setLightboxIndex(i);
    setZoom(1);
    setLightboxOpen(true);
  };

  const closeLightbox = () => setLightboxOpen(false);

  const nextImage = () => {
    setLightboxIndex((i) => (i + 1) % images.length);
    setZoom(1);
  };

  const prevImage = () => {
    setLightboxIndex((i) => (i - 1 + images.length) % images.length);
    setZoom(1);
  };

  const zoomIn = () => setZoom((z) => Math.min(3, z + 0.25));
  const zoomOut = () => setZoom((z) => Math.max(0.5, z - 0.25));
  const resetZoom = () => setZoom(1);

  // Keyboard nav
  const handleKeyDown = useCallback(
    (e) => {
      if (!lightboxOpen) return;

      if (e.key === "Escape") closeLightbox();
      if (e.key === "ArrowRight") nextImage();
      if (e.key === "ArrowLeft") prevImage();
    },
    [lightboxOpen]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () =>
      document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // Touch swipe
  const handleTouchStart = (e) => {
    if (!lightboxOpen) return;
    const x = e.touches[0].clientX;
    touchStartXRef.current = x;
    touchEndXRef.current = x;
  };

  const handleTouchMove = (e) => {
    if (!lightboxOpen) return;
    touchEndXRef.current = e.touches[0].clientX;
  };

  const handleTouchEnd = () => {
    if (!lightboxOpen) return;

    const delta = touchEndXRef.current - touchStartXRef.current;
    if (Math.abs(delta) < SWIPE_THRESHOLD) return;

    if (delta < 0) nextImage();
    else prevImage();

    setZoom(1);
  };

  // ⭐ DOUBLE-TAP / DOUBLE-CLICK ZOOM
  const handleDoubleTap = () => {
    const now = Date.now();
    const delta = now - lastTapRef.current;
    lastTapRef.current = now;

    if (delta < DOUBLE_TAP_THRESHOLD) {
      // Toggle zoom
      setZoom((z) => (z === 1 ? 2 : 1));
    }
  };

  const activeImage = images[lightboxIndex];

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <Link to="/" className="text-blue-600 hover:underline">
        ← Back
      </Link>

      <div className="grid lg:grid-cols-3 gap-10 mt-4">
        <div className="lg:col-span-2 space-y-4">
          {/* MAIN IMAGE */}
          <div
            className="h-72 md:h-96 rounded-xl overflow-hidden cursor-zoom-in"
            onClick={() => openLightbox(lightboxIndex)}
          >
            <img
              src={activeImage}
              className="w-full h-full object-cover"
            />
          </div>

          {/* THUMBS */}
          <div className="flex gap-2 overflow-x-auto">
            {images.map((img, idx) => (
              <button
                key={idx}
                onClick={() => openLightbox(idx)}
                className={`h-16 w-24 rounded-lg overflow-hidden border ${
                  idx === lightboxIndex
                    ? "border-blue-500"
                    : "border-transparent opacity-70 hover:opacity-100"
                }`}
              >
                <img
                  src={img}
                  className="w-full h-full object-cover"
                />
              </button>
            ))}
          </div>
        </div>

        {/* RIGHT SIDE DETAILS */}
        <div className="space-y-4">
          <h1 className="text-3xl font-bold">
            {listing.title}
          </h1>
          <p className="text-gray-600">{listing.address}</p>

          <div className="bg-white rounded-xl shadow p-4">
            <p className="text-2xl font-semibold text-blue-600">
              ${listing.price?.toLocaleString?.()}
            </p>
            <p className="text-green-600">
              ${listing.downPayment?.toLocaleString?.()} down
            </p>
          </div>

          {listing.description && (
            <div className="bg-white rounded-xl shadow p-4 text-sm">
              <p>{listing.description}</p>
            </div>
          )}
        </div>
      </div>

      {/* LIGHTBOX */}
      {lightboxOpen && (
        <div
          className="fixed inset-0 bg-black/80 flex items-center justify-center z-50"
          onClick={closeLightbox}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* TOP BAR */}
            <div className="flex justify-between mb-3 text-white text-sm">
              <div>
                {lightboxIndex + 1}/{images.length}
              </div>
              <button
                onClick={closeLightbox}
                className="px-3 py-1 border border-white/40 rounded-full"
              >
                Close ✕
              </button>
            </div>

            {/* IMAGE WITH SWIPE + DOUBLE-TAP */}
            <div
              className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]"
              onTouchStart={handleTouchStart}
              onTouchMove={handleTouchMove}
              onTouchEnd={(e) => {
                handleTouchEnd();
                handleDoubleTap();
              }}
              onDoubleClick={handleDoubleTap}
            >
              <img
                src={activeImage}
                className="max-h-[80vh] max-w-full transition-transform"
                style={{ transform: `scale(${zoom})` }}
              />
            </div>

            {/* CONTROLS */}
            <div className="flex justify-between mt-3 text-white text-sm">
              <div className="flex gap-2">
                <button
                  onClick={prevImage}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  ← Prev
                </button>
                <button
                  onClick={nextImage}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  Next →
                </button>
              </div>

              <div className="flex items-center gap-2">
                <button
                  onClick={zoomOut}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  −
                </button>
                <span className="opacity-70">
                  {(zoom * 100).toFixed(0)}%
                </span>
                <button
                  onClick={zoomIn}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  +
                </button>
                <button
                  onClick={resetZoom}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
