import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function Navbar() {
  const { user, role } = useAuth();
  const navigate = useNavigate();

  const isAdmin = role === "admin";

  const handleLogout = async () => {
    const { logout } = await import("../firebase/auth.js");
    await logout();
    navigate("/login");
  };

  return (
    <nav className="flex items-center justify-between px-10 py-4 border-b border-gray-200 bg-white shadow-sm">
      
      {/* LOGO */}
      <Link to="/" className="flex items-center gap-3">
        <img 
          src="/logo.png" 
          alt="HI AWTO Logo" 
          className="h-16 w-auto"   // 64px tall, correct visual size
        />
      </Link>

      {/* NAV LINKS */}
      <div className="flex gap-8 items-center text-sm md:text-base text-slate-800 font-medium">
        <Link to="/" className="hover:text-blue-600">Home</Link>

        {user && (
          <>
            <Link to="/my-listings" className="hover:text-blue-600">My Listings</Link>
            <Link to="/messages" className="hover:text-blue-600">Messages</Link>
            <Link to="/profile" className="hover:text-blue-600">Profile</Link>
          </>
        )}

        {user && isAdmin && (
          <Link to="/admin/messages" className="hover:text-green-600">Admin</Link>
        )}

        {!user && (
          <>
            <Link to="/login" className="hover:text-blue-600">Login</Link>
            <Link to="/register" className="hover:text-blue-600">Register</Link>
          </>
        )}

        {user && (
          <button
            onClick={handleLogout}
            className="text-red-600 hover:text-red-700 transition"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}
