import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllListings } from "../firebase/listings.js";
import "./Home.css";

export default function Home() {
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const data = await getAllListings();
        setListings(data);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <div className="home-container">
      {/* Logo */}
      <div className="home-logo-wrapper">
        <img src="/logo.png" alt="HI AWTO Logo" className="home-logo" />
      </div>

      <h1 className="home-title">Lease-to-Own Listings</h1>

      {loading && <div className="loading">Loading listings...</div>}

      {!loading && listings.length === 0 && (
        <div className="empty">No listings yet. Check back soon.</div>
      )}

      <div className="listing-grid">
        {listings.map((l) => (
          <Link
            key={l.id}
            to={`/listing/${l.id}`}
            className="listing-card"
          >
            <h2 className="listing-title">{l.title || "Untitled listing"}</h2>
            <p className="listing-address">{l.address}</p>
            <p className="listing-price">
              ${l.price?.toLocaleString?.() || l.price} total â€¢ $
              {l.downPayment?.toLocaleString?.() || l.downPayment} down
            </p>
          </Link>
        ))}
      </div>
    </div>
  );
}
