// src/pages/CreateListing.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import { createListing } from "../firebase/listings.js";
import { uploadListingImages } from "../firebase/uploadManager.js";
import { analyzeImage } from "../firebase/aiTags.js";

export default function CreateListing() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    title: "",
    address: "",
    price: "",
    downPayment: "",
    description: "",
    featured: false,
  });

  const [imageFiles, setImageFiles] = useState([]);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({
      ...f,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleImagesChange = (e) => {
    const files = Array.from(e.target.files || []);
    setImageFiles(files);
    setImagePreviews(files.map((file) => URL.createObjectURL(file)));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSubmitting(true);

    try {
      // --------------------------
      // 1) Upload images
      // --------------------------
      let imageUrls = [];
      if (imageFiles.length > 0) {
        imageUrls = await uploadListingImages(imageFiles);
      }

      // --------------------------
      // 2) AI TAGGING + CAPTIONING
      // --------------------------
      let aiTags = [];
      let aiCaption = "";

      if (imageUrls.length > 0) {
        // Analyze FIRST image only (recommended)
        const mainImageUrl = imageUrls[0];

        const aiResult = await analyzeImage(mainImageUrl);
        aiTags = aiResult.tags || [];
        aiCaption = aiResult.caption || "";
      }

      // --------------------------
      // 3) Create Firestore listing
      // --------------------------
      const payload = {
        title: form.title.trim(),
        address: form.address.trim(),
        price: form.price ? Number(form.price) : null,
        downPayment: form.downPayment ? Number(form.downPayment) : null,
        description: form.description.trim(),
        featured: !!form.featured,
        imageUrls,
        aiTags,
        aiCaption,
        createdAt: new Date(),
      };

      await createListing(payload);
      navigate("/my-listings");

    } catch (err) {
      console.error(err);
      setError(err?.message || "Failed to create listing.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto px-4 py-10">
      <h1 className="text-2xl md:text-3xl font-bold text-slate-800 mb-6">
        Create a New Listing
      </h1>

      {error && (
        <div className="mb-4 rounded bg-red-50 text-red-700 px-3 py-2 text-sm">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 rounded-xl shadow">

        {/* Title */}
        <div>
          <label className="block text-sm font-medium mb-1">Title</label>
          <input
            type="text"
            name="title"
            value={form.title}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2 text-sm"
            required
          />
        </div>

        {/* Address */}
        <div>
          <label className="block text-sm font-medium mb-1">Address</label>
          <input
            type="text"
            name="address"
            value={form.address}
            onChange={handleChange}
            className="w-full border rounded-lg px-3 py-2 text-sm"
            required
          />
        </div>

        {/* Price + Down payment */}
        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium mb-1">Total Price</label>
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              className="w-full border rounded-lg px-3 py-2 text-sm"
              min="0"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-1">Down Payment</label>
            <input
              type="number"
              name="downPayment"
              value={form.downPayment}
              onChange={handleChange}
              className="w-full border rounded-lg px-3 py-2 text-sm"
              min="0"
              required
            />
          </div>
        </div>

        {/* Description */}
        <div>
          <label className="block text-sm font-medium mb-1">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            rows={4}
            className="w-full border rounded-lg px-3 py-2 text-sm"
          />
        </div>

        {/* Featured */}
        <div className="flex items-center gap-2">
          <input
            id="featured"
            type="checkbox"
            name="featured"
            checked={form.featured}
            onChange={handleChange}
            className="h-4 w-4"
          />
          <label htmlFor="featured" className="text-sm">Mark as featured</label>
        </div>

        {/* Images */}
        <div>
          <label className="block text-sm font-medium mb-1">Listing Images</label>
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={handleImagesChange}
          />

          {imagePreviews.length > 0 && (
            <div className="mt-3 grid grid-cols-3 gap-2">
              {imagePreviews.map((src, idx) => (
                <img key={idx} src={src} className="h-20 w-full object-cover rounded" />
              ))}
            </div>
          )}
        </div>

        {/* Submit */}
        <button
          type="submit"
          disabled={submitting}
          className="px-6 py-2 bg-blue-600 text-white rounded-full"
        >
          {submitting ? "Saving..." : "Create Listing"}
        </button>

      </form>
    </div>
  );
}
