import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { getListing } from "../firebase/listings.js";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function ListingDetails() {
  const { id } = useParams();
  const { user } = useAuth();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      const data = await getListing(id);
      setListing(data);
      setLoading(false);
    })();
  }, [id]);

  if (loading) return <div className="p-4">Loading listing...</div>;
  if (!listing) return <div className="p-4">Listing not found.</div>;

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-2">{listing.title}</h1>
      <p className="text-gray-600 mb-2">{listing.address}</p>
      <p className="mb-2">
        Price: ${listing.price?.toLocaleString?.() || listing.price}
      </p>
      <p className="mb-2">
        Down Payment: $
        {listing.downPayment?.toLocaleString?.() || listing.downPayment}
      </p>
      <p className="mb-2">Lease Terms: {listing.leaseTerms}</p>
      <p className="mb-4 whitespace-pre-wrap">{listing.description}</p>

      {listing.images && listing.images.length > 0 && (
        <div className="grid grid-cols-2 gap-2 mb-4">
          {listing.images.map((url) => (
            <img
              key={url}
              src={url}
              alt="Listing"
              className="w-full h-40 object-cover rounded"
            />
          ))}
        </div>
      )}

      {user && user.uid !== listing.ownerId && (
        <Link
          to="/messages"
          className="inline-block bg-blue-600 text-white px-4 py-2 rounded"
        >
          Contact Seller
        </Link>
      )}
    </div>
  );
}
