import React, { useEffect, useState, useMemo } from "react";
import { Link } from "react-router-dom";
import { getAllListings } from "../firebase/listings.js";

const NEW_LIMIT_MS = 14 * 24 * 60 * 60 * 1000; // 14 days

// Helper: normalize Firestore Timestamp / Date / number / string to Date
function getCreatedAtDate(listing) {
  const raw = listing?.createdAt;
  if (!raw) return null;

  // Firestore Timestamp with toDate()
  if (typeof raw.toDate === "function") return raw.toDate();
  // Firestore Timestamp-like { seconds }
  if (typeof raw.seconds === "number") return new Date(raw.seconds * 1000);
  // Milliseconds number
  if (typeof raw === "number") return new Date(raw);
  // ISO string
  if (typeof raw === "string") return new Date(raw);
  return null;
}

function isNewListing(listing) {
  const d = getCreatedAtDate(listing);
  if (!d || isNaN(d.getTime())) return false;
  return Date.now() - d.getTime() <= NEW_LIMIT_MS;
}

export default function Home() {
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  // Search & filters
  const [searchTerm, setSearchTerm] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const [featuredOnly, setFeaturedOnly] = useState(false);
  const [newOnly, setNewOnly] = useState(false);

  useEffect(() => {
    (async () => {
      try {
        const data = await getAllListings();
        const safe = Array.isArray(data) ? data : [];
        // Sort most recent first using createdAt
        const sorted = [...safe].sort((a, b) => {
          const da = getCreatedAtDate(a);
          const db = getCreatedAtDate(b);
          if (!da && !db) return 0;
          if (!da) return 1;
          if (!db) return -1;
          return db.getTime() - da.getTime();
        });
        setListings(sorted);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // Featured listings: prefer Firestore flag, else fallback to first 3
  const featuredListings = useMemo(() => {
    const explicit = listings.filter((l) => l.featured === true);
    if (explicit.length > 0) return explicit.slice(0, 3);
    return listings.slice(0, 3);
  }, [listings]);

  const [activeFeature, setActiveFeature] = useState(0);
  const currentFeatured =
    featuredListings.length > 0 ? featuredListings[activeFeature] : null;

  const handlePrevFeature = () => {
    if (!featuredListings.length) return;
    setActiveFeature((prev) =>
      prev === 0 ? featuredListings.length - 1 : prev - 1
    );
  };

  const handleNextFeature = () => {
    if (!featuredListings.length) return;
    setActiveFeature((prev) =>
      prev === featuredListings.length - 1 ? 0 : prev + 1
    );
  };

  // Filtered listings for the main grid
  const filteredListings = useMemo(() => {
    return listings.filter((l) => {
      const text = `${l.title || ""} ${l.address || ""}`.toLowerCase();
      const term = searchTerm.trim().toLowerCase();
      if (term && !text.includes(term)) return false;

      const price = Number(l.price ?? NaN);
      const min = Number(minPrice);
      const max = Number(maxPrice);

      if (minPrice && !isNaN(min) && !isNaN(price) && price < min) return false;
      if (maxPrice && !isNaN(max) && !isNaN(price) && price > max) return false;

      if (featuredOnly && l.featured !== true) return false;
      if (newOnly && !isNewListing(l)) return false;

      return true;
    });
  }, [listings, searchTerm, minPrice, maxPrice, featuredOnly, newOnly]);

  const testimonials = [
    {
      name: "Jordan M.",
      role: "First-time buyer",
      quote:
        "HI AWTO made it possible for me to move into a home I love while working toward ownership. The process was clear and surprisingly flexible.",
    },
    {
      name: "Alicia & Mark R.",
      role: "Growing family",
      quote:
        "We weren’t ready for a traditional mortgage. Lease-to-own gave us time to clean up our finances without putting our dream home on hold.",
    },
    {
      name: "Chris T.",
      role: "Self-employed",
      quote:
        "As an entrepreneur, my income doesn’t fit a normal box. HI AWTO looked at my situation holistically and gave me a real path to owning.",
    },
  ];

  const [openFaq, setOpenFaq] = useState(null);
  const faqs = [
    {
      q: "What is lease-to-own housing?",
      a: "Lease-to-own lets you live in the home today while working toward ownership over time. Part of your payment can go toward your future purchase.",
    },
    {
      q: "Do I need perfect credit?",
      a: "No. We focus on your whole financial picture, not just a single credit score. Many residents use HI AWTO as a bridge to mortgage readiness.",
    },
    {
      q: "Can I bring my own home into the program?",
      a: "Yes. In many cases you can identify a home you love and explore whether it qualifies for HI AWTO’s lease-to-own structure.",
    },
    {
      q: "Is there a long-term commitment?",
      a: "You’ll have a clearly defined lease term and purchase option. You’ll always know your timelines and your path to buy before you move in.",
    },
  ];

  return (
    <div className="w-full bg-gray-50 min-h-screen">
      {/* =============================== */}
      {/* HERO SECTION */}
      {/* =============================== */}
      <section className="hero-gradient relative overflow-hidden pt-20 pb-24 px-4 text-center">
        <div className="hero-shape hero-shape-1"></div>
        <div className="hero-shape hero-shape-2"></div>
        <div className="hero-shape hero-shape-3"></div>

        <div className="relative z-10 max-w-4xl mx-auto">
          <img
            src="/logo.png"
            alt="HI AWTO Logo"
            className="h-24 w-auto mx-auto mb-4 animate-fade-in"
          />

          <h1 className="text-3xl md:text-4xl font-extrabold text-white tracking-wide drop-shadow-lg animate-fade-in">
            HERE IS ANOTHER WAY TO OWN
          </h1>

          <p className="text-lg mt-4 text-blue-100 animate-fade-in-delayed">
            Turn today’s rent into tomorrow’s ownership with flexible
            lease-to-own options.
          </p>

          {/* CTAs */}
          <div className="flex flex-col sm:flex-row justify-center gap-4 mt-8 animate-fade-in-delayed">
            <Link
              to="/register"
              className="inline-flex items-center justify-center px-8 py-3 rounded-full bg-white text-blue-700 font-semibold shadow hover:shadow-lg transition"
            >
              Get Started
            </Link>
            <a
              href="#listings"
              className="inline-flex items-center justify-center px-8 py-3 rounded-full border border-blue-100 text-blue-50 font-semibold hover:bg-white/10 transition"
            >
              Browse Listings
            </a>
          </div>
        </div>
      </section>

      {/* =============================== */}
      {/* HOW IT WORKS (summary) */}
      {/* =============================== */}
      <section className="max-w-6xl mx-auto px-4 py-16">
        <h2 className="text-2xl md:text-3xl font-bold text-center text-slate-800">
          How It Works
        </h2>

        <p className="text-center text-gray-600 mt-2 mb-10">
          Three simple steps to move into your next home and work toward owning
          it.
        </p>

        <div className="grid md:grid-cols-3 gap-10 mt-10">
          <div className="bg-white p-8 rounded-xl shadow hover:shadow-lg transition">
            <div className="text-blue-600 text-4xl font-extrabold mb-3">1</div>
            <h3 className="text-xl font-semibold text-slate-800 mb-2">
              Find a Home
            </h3>
            <p className="text-gray-600">
              Explore HI AWTO listings or bring your own eligible home into the
              program.
            </p>
          </div>

          <div className="bg-white p-8 rounded-xl shadow hover:shadow-lg transition">
            <div className="text-blue-600 text-4xl font-extrabold mb-3">2</div>
            <h3 className="text-xl font-semibold text-slate-800 mb-2">
              Qualify & Move In
            </h3>
            <p className="text-gray-600">
              Apply with flexible criteria. Once approved, you move in as a
              resident with a path to own.
            </p>
          </div>

          <div className="bg-white p-8 rounded-xl shadow hover:shadow-lg transition">
            <div className="text-blue-600 text-4xl font-extrabold mb-3">3</div>
            <h3 className="text-xl font-semibold text-slate-800 mb-2">
              Build Toward Ownership
            </h3>
            <p className="text-gray-600">
              Live in the home, build stability, and work toward exercising your
              option to buy when the time is right.
            </p>
          </div>
        </div>

        <div className="text-center mt-8">
          <Link
            to="/about"
            className="text-blue-600 font-semibold hover:underline"
          >
            Learn more about how HI AWTO works →
          </Link>
        </div>
      </section>

      {/* =============================== */}
      {/* FEATURED CAROUSEL */}
      {/* =============================== */}
      <section className="max-w-6xl mx-auto px-4 pb-16">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-slate-800">Featured Homes</h2>

          {featuredListings.length > 1 && (
            <div className="flex gap-2">
              <button
                onClick={handlePrevFeature}
                className="px-3 py-1 rounded-full border text-sm text-slate-700 hover:bg-gray-100"
              >
                Prev
              </button>
              <button
                onClick={handleNextFeature}
                className="px-3 py-1 rounded-full border text-sm text-slate-700 hover:bg-gray-100"
              >
                Next
              </button>
            </div>
          )}
        </div>

        {loading && (
          <div className="text-center text-gray-500 py-10">
            Loading featured homes…
          </div>
        )}

        {!loading && !currentFeatured && (
          <div className="text-center text-gray-500 py-10">
            Featured homes will appear here once listings are added.
          </div>
        )}

        {!loading && currentFeatured && (
          <Link
            to={`/listing/${currentFeatured.id}`}
            className="block bg-white rounded-2xl shadow-md hover:shadow-xl transition overflow-hidden group"
          >
            <div className="h-52 w-full bg-gradient-to-r from-blue-500 to-green-400 opacity-80 group-hover:opacity-100 transition" />

            <div className="p-6 flex flex-col md:flex-row md:items-center md:justify-between gap-4">
              <div>
                <h3 className="font-semibold text-xl text-slate-800 flex items-center gap-2">
                  {currentFeatured.title || "Untitled Listing"}
                  {currentFeatured.featured && (
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full bg-yellow-100 text-yellow-800 text-xs font-semibold">
                      Featured
                    </span>
                  )}
                  {isNewListing(currentFeatured) && (
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full bg-green-100 text-green-700 text-xs font-semibold">
                      New
                    </span>
                  )}
                </h3>
                <p className="text-gray-600 text-sm mt-1">
                  {currentFeatured.address || "No address provided"}
                </p>
              </div>

              <div className="text-right md:text-right">
                <p className="text-blue-600 font-semibold text-lg">
                  $
                  {currentFeatured.price?.toLocaleString?.() ||
                    currentFeatured.price}{" "}
                  total
                </p>
                <p className="text-green-600 font-medium text-sm">
                  $
                  {currentFeatured.downPayment?.toLocaleString?.() ||
                    currentFeatured.downPayment}{" "}
                  down
                </p>
              </div>
            </div>
          </Link>
        )}
      </section>

      {/* =============================== */}
      {/* LISTINGS SECTION WITH FILTERS */}
      {/* =============================== */}
      <section id="listings" className="max-w-6xl mx-auto px-4 pb-24">
        <div className="flex flex-col gap-4 md:flex-row md:items-end md:justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold text-slate-800">
              All Available Homes
            </h2>
            <p className="text-gray-500 text-sm mt-1">
              Filter by price, search by city or address, and highlight featured
              or new homes.
            </p>
          </div>

          <div className="flex flex-col md:flex-row gap-3 md:items-end">
            <input
              type="text"
              placeholder="Search by title or address"
              className="border rounded-lg px-3 py-2 text-sm w-full md:w-56"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />

            <div className="flex gap-2">
              <input
                type="number"
                placeholder="Min price"
                className="border rounded-lg px-3 py-2 text-sm w-24"
                value={minPrice}
                onChange={(e) => setMinPrice(e.target.value)}
              />
              <input
                type="number"
                placeholder="Max price"
                className="border rounded-lg px-3 py-2 text-sm w-24"
                value={maxPrice}
                onChange={(e) => setMaxPrice(e.target.value)}
              />
            </div>

            <div className="flex gap-3 text-xs md:text-sm">
              <label className="inline-flex items-center gap-1">
                <input
                  type="checkbox"
                  checked={featuredOnly}
                  onChange={(e) => setFeaturedOnly(e.target.checked)}
                />
                <span>Featured only</span>
              </label>

              <label className="inline-flex items-center gap-1">
                <input
                  type="checkbox"
                  checked={newOnly}
                  onChange={(e) => setNewOnly(e.target.checked)}
                />
                <span>New only</span>
              </label>
            </div>
          </div>
        </div>

        {loading && (
          <div className="text-center text-gray-500 text-lg mt-10">
            Loading listings…
          </div>
        )}

        {!loading && filteredListings.length === 0 && (
          <div className="text-center text-gray-500 text-lg mt-10">
            No listings match your filters yet. Try adjusting your search.
          </div>
        )}

        {!loading && filteredListings.length > 0 && (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredListings.map((listing) => (
              <Link
                key={listing.id}
                to={`/listing/${listing.id}`}
                className="bg-white rounded-xl shadow hover:shadow-xl transition p-5 overflow-hidden group"
              >
                <div className="h-40 w-full bg-gray-200 rounded-lg mb-4 group-hover:opacity-90 transition" />

                <div className="flex items-center gap-2 mb-2">
                  {listing.featured && (
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full bg-yellow-100 text-yellow-800 text-xs font-semibold">
                      Featured
                    </span>
                  )}
                  {isNewListing(listing) && (
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full bg-green-100 text-green-700 text-xs font-semibold">
                      New
                    </span>
                  )}
                </div>

                <h3 className="font-semibold text-lg text-slate-800">
                  {listing.title || "Untitled Listing"}
                </h3>

                <p className="text-gray-600 text-sm mt-1">
                  {listing.address || "No address provided"}
                </p>

                <p className="text-blue-600 font-semibold mt-3">
                  ${listing.price?.toLocaleString?.() || listing.price} total
                </p>

                <p className="text-green-600 font-medium text-sm">
                  $
                  {listing.downPayment?.toLocaleString?.() ||
                    listing.downPayment}{" "}
                  down
                </p>
              </Link>
            ))}
          </div>
        )}
      </section>

      {/* =============================== */}
      {/* TESTIMONIALS */}
      {/* =============================== */}
      <section className="bg-white border-t border-gray-100">
        <div className="max-w-6xl mx-auto px-4 py-16">
          <h2 className="text-2xl md:text-3xl font-bold text-center text-slate-800">
            What Residents Are Saying
          </h2>
          <p className="text-center text-gray-600 mt-2 mb-10">
            Real stories from people using HI AWTO to move toward ownership.
          </p>

          <div className="grid md:grid-cols-3 gap-8">
            {testimonials.map((t, idx) => (
              <div
                key={idx}
                className="bg-gray-50 border border-gray-100 rounded-xl p-6 shadow-sm"
              >
                <p className="text-gray-700 italic mb-4">“{t.quote}”</p>
                <p className="font-semibold text-slate-800">{t.name}</p>
                <p className="text-sm text-gray-500">{t.role}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* =============================== */}
      {/* FAQ + FINAL CTA (kept on /about as well) */}
      {/* =============================== */}
      <section className="max-w-4xl mx-auto px-4 py-16">
        <h2 className="text-2xl md:text-3xl font-bold text-center text-slate-800">
          Frequently Asked Questions
        </h2>
        <p className="text-center text-gray-600 mt-2 mb-8">
          Have questions? We’ve answered some of the most common ones below.
        </p>

        <div className="space-y-4">
          {faqs.map((item, idx) => {
            const isOpen = openFaq === idx;
            return (
              <div
                key={idx}
                className="bg-white rounded-lg border border-gray-200 shadow-sm"
              >
                <button
                  type="button"
                  onClick={() => setOpenFaq(isOpen ? null : idx)}
                  className="w-full flex justify-between items-center px-4 py-3 text-left"
                >
                  <span className="font-medium text-slate-800">
                    {item.q}
                  </span>
                  <span className="text-blue-600 font-bold text-xl">
                    {isOpen ? "−" : "+"}
                  </span>
                </button>
                {isOpen && (
                  <div className="px-4 pb-4 text-gray-600 text-sm">
                    {item.a}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </section>

      <section className="bg-blue-600">
        <div className="max-w-4xl mx-auto px-4 py-10 text-center text-white">
          <h2 className="text-2xl md:text-3xl font-bold">
            Ready to explore another way to own?
          </h2>
          <p className="mt-3 text-blue-100">
            Start your HI AWTO journey in just a few minutes.
          </p>
          <div className="mt-6 flex flex-col sm:flex-row gap-4 justify-center">
            <Link
              to="/register"
              className="inline-flex items-center justify-center px-8 py-3 rounded-full bg-white text-blue-700 font-semibold shadow hover:shadow-lg transition"
            >
              Get Started
            </Link>
            <Link
              to="/login"
              className="inline-flex items-center justify-center px-8 py-3 rounded-full border border-blue-200 text-white font-semibold hover:bg-white/10 transition"
            >
              Already have an account? Log in
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
}
