// src/pages/Profile.jsx
import React, { useEffect, useState } from "react";
import { useAuth } from "../firebase/AuthContext.jsx";
import { uploadAvatar } from "../firebase/uploadManager.js";
import { auth } from "../firebase/auth.js";
import { db } from "../firebase/config.js";
import {
  doc,
  getDoc,
  setDoc,
  serverTimestamp,
} from "firebase/firestore";
import { updateProfile } from "firebase/auth";

export default function Profile() {
  const { user } = useAuth(); // Firebase Auth user
  const [displayName, setDisplayName] = useState(user?.displayName || "");
  const [phone, setPhone] = useState("");
  const [bio, setBio] = useState("");

  const [avatarPreview, setAvatarPreview] = useState(user?.photoURL || "");
  const [avatarFile, setAvatarFile] = useState(null);

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  // Load extra profile data from Firestore
  useEffect(() => {
    if (!user) {
      setLoading(false);
      return;
    }

    (async () => {
      setLoading(true);
      setError("");
      try {
        const ref = doc(db, "users", user.uid);
        const snap = await getDoc(ref);
        if (snap.exists()) {
          const data = snap.data();
          if (data.displayName && !displayName) {
            setDisplayName(data.displayName);
          }
          if (data.phone) setPhone(data.phone);
          if (data.bio) setBio(data.bio);
          if (data.photoURL && !avatarPreview) {
            setAvatarPreview(data.photoURL);
          }
        }
      } catch (err) {
        console.error(err);
        setError("Failed to load profile.");
      } finally {
        setLoading(false);
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user]);

  const handleAvatarChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    setAvatarFile(file);
    setAvatarPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) return;

    setError("");
    setStatus("");
    setSaving(true);

    try {
      let photoURL = avatarPreview || user.photoURL || "";

      // Upload new avatar if provided
      if (avatarFile) {
        const uploadedUrl = await uploadAvatar(avatarFile);
        if (uploadedUrl) {
          photoURL = uploadedUrl;
        }
      }

      // Update Firebase Auth profile
      await updateProfile(auth.currentUser, {
        displayName: displayName || user.displayName || "",
        photoURL: photoURL || null,
      });

      // Update Firestore profile document
      const ref = doc(db, "users", user.uid);
      await setDoc(
        ref,
        {
          displayName: displayName || null,
          phone: phone || null,
          bio: bio || null,
          photoURL: photoURL || null,
          updatedAt: serverTimestamp(),
        },
        { merge: true }
      );

      setStatus("Profile updated successfully.");
    } catch (err) {
      console.error(err);
      setError(err.message || "Failed to update profile.");
    } finally {
      setSaving(false);
    }
  };

  if (!user) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-600 text-sm">
          You need to be logged in to view your profile.
        </p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Loading profile…</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto px-4 py-10">
      <h1 className="text-2xl md:text-3xl font-bold text-slate-800 mb-2">
        My Profile
      </h1>
      <p className="text-sm text-gray-500 mb-6">
        Update your account details and profile photo.
      </p>

      {error && (
        <div className="mb-3 rounded bg-red-50 text-red-700 px-3 py-2 text-sm">
          {error}
        </div>
      )}
      {status && (
        <div className="mb-3 rounded bg-green-50 text-green-700 px-3 py-2 text-sm">
          {status}
        </div>
      )}

      <form
        onSubmit={handleSubmit}
        className="bg-white rounded-xl shadow p-6 space-y-6"
      >
        {/* Avatar */}
        <div className="flex items-center gap-4">
          <div className="h-16 w-16 rounded-full bg-slate-200 overflow-hidden flex items-center justify-center">
            {avatarPreview ? (
              <img
                src={avatarPreview}
                alt="Avatar preview"
                className="w-full h-full object-cover"
              />
            ) : (
              <span className="text-slate-500 text-xs">No photo</span>
            )}
          </div>
          <div>
            <p className="text-sm font-medium text-slate-800">
              Profile photo
            </p>
            <p className="text-xs text-slate-500 mb-1">
              Upload a square image for best results.
            </p>
            <input
              type="file"
              accept="image/*"
              onChange={handleAvatarChange}
              className="text-xs text-slate-600"
            />
          </div>
        </div>

        {/* Name */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-1">
            Full name
          </label>
          <input
            type="text"
            value={displayName}
            onChange={(e) => setDisplayName(e.target.value)}
            className="w-full border rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500"
          />
        </div>

        {/* Email (read-only) */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-1">
            Email
          </label>
          <input
            type="email"
            value={user.email || ""}
            disabled
            className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 text-slate-500"
          />
        </div>

        {/* Phone */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-1">
            Phone (optional)
          </label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="w-full border rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500"
            placeholder="(555) 555-5555"
          />
        </div>

        {/* Bio */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-1">
            About you (optional)
          </label>
          <textarea
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            rows={4}
            className="w-full border rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500"
            placeholder="Tell us about yourself or your home search."
          />
        </div>

        <div className="pt-2">
          <button
            type="submit"
            disabled={saving}
            className="inline-flex items-center justify-center px-6 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700 disabled:opacity-60"
          >
            {saving ? "Saving…" : "Save changes"}
          </button>
        </div>
      </form>
    </div>
  );
}
