// src/pages/ViewThread.jsx
import React, { useEffect, useState, useRef } from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  getThreadMessages,
  sendMessage,
  setThreadTyping,
} from "../firebase/messages.js";
import { db } from "../firebase/config.js";
import { doc, onSnapshot } from "firebase/firestore";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  const [newMessage, setNewMessage] = useState("");

  // Typing indicator state
  const [typingUsers, setTypingUsers] = useState([]);
  const typingActiveRef = useRef(false);
  const typingTimeoutRef = useRef(null);

  useEffect(() => {
    if (!threadId) return;

    (async () => {
      setLoading(true);
      try {
        const t = await getThread(threadId);
        const msgs = await getThreadMessages(threadId);
        setThread(t);
        setMessages(msgs || []);
      } finally {
        setLoading(false);
      }
    })();
  }, [threadId]);

  // üîµ Subscribe to thread doc to listen for typing map
  useEffect(() => {
    if (!threadId || !user) return;

    const ref = doc(db, "threads", threadId);
    const unsub = onSnapshot(ref, (snap) => {
      if (!snap.exists()) return;
      const data = snap.data();
      const typingMap = data.typing || {};

      const active = Object.entries(typingMap)
        .filter(([uid, isOn]) => uid !== user.uid && !!isOn)
        .map(([uid]) => uid);

      setTypingUsers(active);
    });

    return unsub;
  }, [threadId, user]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !user) return;

    setSending(true);
    try {
      await sendMessage(threadId, {
        text: newMessage.trim(),
        senderId: user.uid,
        senderName: user.displayName || user.email || "User",
      });
      setNewMessage("");
      // Stop typing when message is sent
      await setThreadTyping(threadId, user.uid, false);
      typingActiveRef.current = false;
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
        typingTimeoutRef.current = null;
      }
      // Refresh messages (basic version)
      const msgs = await getThreadMessages(threadId);
      setMessages(msgs || []);
    } catch (err) {
      console.error("Failed to send message", err);
    } finally {
      setSending(false);
    }
  };

  // Handle local user typing, with small inactivity timeout
  const handleInputChange = async (e) => {
    const value = e.target.value;
    setNewMessage(value);

    if (!user || !threadId) return;

    // If user starts typing and wasn't marked as typing yet
    if (!typingActiveRef.current) {
      typingActiveRef.current = true;
      setThreadTyping(threadId, user.uid, true).catch(console.error);
    }

    // Reset inactivity timeout to clear typing
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
    typingTimeoutRef.current = setTimeout(() => {
      typingActiveRef.current = false;
      setThreadTyping(threadId, user.uid, false).catch(console.error);
      typingTimeoutRef.current = null;
    }, 2000); // 2s after last keypress
  };

  const handleBlur = () => {
    if (!user || !threadId) return;
    typingActiveRef.current = false;
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
      typingTimeoutRef.current = null;
    }
    setThreadTyping(threadId, user.uid, false).catch(console.error);
  };

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Loading conversation‚Ä¶</p>
      </div>
    );
  }

  if (!thread) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Thread not found.</p>
        <Link to="/messages" className="text-blue-600 text-sm hover:underline mt-2 inline-block">
          ‚Üê Back to messages
        </Link>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto px-4 py-10 flex flex-col gap-4">
      <div className="flex items-center justify-between mb-2">
        <div>
          <Link
            to="/messages"
            className="text-blue-600 text-xs hover:underline inline-block mb-1"
          >
            ‚Üê Back to messages
          </Link>
          <h1 className="text-xl font-semibold text-slate-800">
            Conversation
          </h1>
        </div>
      </div>

      {/* Messages */}
      <div className="bg-white rounded-xl shadow p-4 max-h-[60vh] overflow-y-auto space-y-3">
        {messages.length === 0 && (
          <p className="text-xs text-gray-500">No messages yet. Say hello!</p>
        )}

        {messages.map((m) => {
          const isMine = m.senderId === user?.uid;
          return (
            <div
              key={m.id}
              className={`flex ${isMine ? "justify-end" : "justify-start"}`}
            >
              <div
                className={`max-w-[75%] rounded-2xl px-3 py-2 text-sm ${
                  isMine
                    ? "bg-blue-600 text-white rounded-br-sm"
                    : "bg-slate-100 text-slate-800 rounded-bl-sm"
                }`}
              >
                {!isMine && (
                  <div className="text-[11px] font-semibold mb-0.5 opacity-80">
                    {m.senderName || "Someone"}
                  </div>
                )}
                <div>{m.text}</div>
              </div>
            </div>
          );
        })}

        {/* üîµ Typing indicator */}
        {typingUsers.length > 0 && (
          <div className="mt-2 text-xs text-gray-500 italic">
            {typingUsers.length === 1
              ? "Someone is typing‚Ä¶"
              : "Several people are typing‚Ä¶"}
          </div>
        )}
      </div>

      {/* Composer */}
      <form
        onSubmit={handleSend}
        className="bg-white rounded-xl shadow p-3 flex items-center gap-2"
      >
        <textarea
          rows={2}
          value={newMessage}
          onChange={handleInputChange}
          onBlur={handleBlur}
          placeholder="Type your message‚Ä¶"
          className="flex-1 border rounded-lg px-3 py-2 text-sm resize-none focus:outline-none focus:ring-2 focus:ring-blue-500/40 focus:border-blue-500"
        />
        <button
          type="submit"
          disabled={sending || !newMessage.trim()}
          className="inline-flex items-center justify-center px-4 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700 disabled:opacity-50"
        >
          {sending ? "Sending‚Ä¶" : "Send"}
        </button>
      </form>
    </div>
  );
}
