import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import { Menu, X } from "lucide-react";

export default function Navbar() {
  const { user, role } = useAuth();
  const navigate = useNavigate();
  const isAdmin = role === "admin";

  const [menuOpen, setMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleLogout = async () => {
    const { logout } = await import("../firebase/auth.js");
    await logout();
    navigate("/login");
  };

  useEffect(() => {
    const onScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <nav
      className={`sticky top-0 z-50 transition-all duration-300 ${
        isScrolled ? "bg-white shadow-md" : "bg-transparent"
      }`}
    >
      <div className="flex items-center justify-between px-6 py-4">
        {/* LEFT: Logo / Name */}
        <Link
          to="/"
          className="flex items-center gap-3 font-bold text-xl text-blue-600"
          onClick={() => setMenuOpen(false)}
        >
          HI AWTO
        </Link>

        {/* DESKTOP LINKS */}
        <div className="hidden md:flex gap-8 items-center text-base text-slate-800 font-medium">
          <Link to="/" className="hover:text-blue-600">
            Home
          </Link>
          <Link to="/about" className="hover:text-blue-600">
            How It Works
          </Link>

          {user && (
            <>
              <Link to="/my-listings" className="hover:text-blue-600">
                My Listings
              </Link>
              <Link to="/messages" className="hover:text-blue-600">
                Messages
              </Link>
              <Link to="/profile" className="hover:text-blue-600">
                Profile
              </Link>
            </>
          )}

          {user && isAdmin && (
            <Link to="/admin/messages" className="hover:text-green-600 font-semibold">
              Admin
            </Link>
          )}

          {!user && (
            <>
              <Link to="/login" className="hover:text-blue-600">
                Login
              </Link>
              <Link to="/register" className="hover:text-blue-600">
                Register
              </Link>
            </>
          )}

          {user && (
            <button
              onClick={handleLogout}
              className="text-red-500 hover:text-red-600 transition"
            >
              Logout
            </button>
          )}
        </div>

        {/* MOBILE MENU BUTTON */}
        <button
          className="md:hidden text-slate-800"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? <X size={26} /> : <Menu size={26} />}
        </button>
      </div>

      {/* MOBILE MENU */}
      {menuOpen && (
        <div className="md:hidden bg-white shadow-lg px-6 py-4 space-y-4 text-slate-800 text-lg font-medium animate-slide-down">
          <Link to="/" onClick={() => setMenuOpen(false)}>
            Home
          </Link>
          <Link to="/about" onClick={() => setMenuOpen(false)}>
            How It Works
          </Link>

          {user && (
            <>
              <Link to="/my-listings" onClick={() => setMenuOpen(false)}>
                My Listings
              </Link>
              <Link to="/messages" onClick={() => setMenuOpen(false)}>
                Messages
              </Link>
              <Link to="/profile" onClick={() => setMenuOpen(false)}>
                Profile
              </Link>
            </>
          )}

          {user && isAdmin && (
            <Link
              to="/admin/messages"
              className="text-green-600 font-semibold"
              onClick={() => setMenuOpen(false)}
            >
              Admin
            </Link>
          )}

          {!user && (
            <>
              <Link to="/login" onClick={() => setMenuOpen(false)}>
                Login
              </Link>
              <Link to="/register" onClick={() => setMenuOpen(false)}>
                Register
              </Link>
            </>
          )}

          {user && (
            <button
              onClick={() => {
                setMenuOpen(false);
                handleLogout();
              }}
              className="text-red-500"
            >
              Logout
            </button>
          )}
        </div>
      )}
    </nav>
  );
}
