// src/pages/ViewThread.jsx
import React, {
  useEffect,
  useState,
  useRef,
  useCallback,
} from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  sendMessage,
  setThreadTyping,
  markThreadMessagesRead,
} from "../firebase/messages.js";
import { uploadChatAttachmentsWithProgress } from "../firebase/uploadManager.js";
import { db } from "../firebase/config.js";
import {
  doc,
  onSnapshot,
  collection,
  query,
  orderBy,
} from "firebase/firestore";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);

  const [sending, setSending] = useState(false);
  const [newMessage, setNewMessage] = useState("");

  const [attachmentFiles, setAttachmentFiles] = useState([]);
  const [attachmentPreviews, setAttachmentPreviews] = useState([]);
  const [uploadProgress, setUploadProgress] = useState([]);
  const [uploadError, setUploadError] = useState("");
  const fileInputRef = useRef(null);

  const [isDragging, setIsDragging] = useState(false);
  const dragCounterRef = useRef(0);

  const [typingUsers, setTypingUsers] = useState([]);
  const typingTimeoutRef = useRef(null);
  const typingActiveRef = useRef(false);

  // LIGHTBOX
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  const touchStartXRef = useRef(null);
  const touchEndXRef = useRef(null);
  const SWIPE_THRESHOLD = 50;

  const lastTapRef = useRef(0);
  const DOUBLE_TAP_THRESHOLD = 250;

  // Allowed file types
  const allowedTypes = [
    "image/jpeg",
    "image/png",
    "image/webp",
    "image/gif",
    "video/mp4",
    "video/quicktime",
    "application/pdf",
    "application/msword",
    "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
  ];
  const MAX_FILE_MB = 25;
  const MAX_TOTAL_MB = 50;
  const bytesToMB = (b) => b / (1024 * 1024);

  // Load thread metadata
  useEffect(() => {
    if (!threadId) return;
    (async () => {
      const t = await getThread(threadId);
      setThread(t || null);
      setLoading(false);
    })();
  }, [threadId]);

  // Real-time messages
  useEffect(() => {
    if (!threadId) return;
    const msgsRef = collection(db, `threads/${threadId}/messages`);
    const q = query(msgsRef, orderBy("createdAt", "asc"));
    const unsub = onSnapshot(q, (snap) =>
      setMessages(snap.docs.map((d) => ({ id: d.id, ...d.data() })))
    );
    return unsub;
  }, [threadId]);

  // Real-time typing indicator
  useEffect(() => {
    if (!threadId || !user) return;
    const ref = doc(db, "threads", threadId);
    const unsub = onSnapshot(ref, (snap) => {
      if (!snap.exists()) return;
      const data = snap.data();
      const typing = data.typing || {};
      const others = Object.entries(typing)
        .filter(([uid, on]) => uid !== user.uid && on)
        .map(([uid]) => uid);
      setTypingUsers(others);
      setThread({ id: snap.id, ...data });
    });
    return unsub;
  }, [threadId, user]);

  // Mark messages read
  useEffect(() => {
    if (!user || !threadId || messages.length === 0) return;
    markThreadMessagesRead(threadId, user.uid);
  }, [messages, threadId, user]);

  // Validate + process files
  function processFiles(files) {
    let valid = [];
    let previews = [];
    let errors = [];

    files.forEach((file) => {
      const sizeMb = bytesToMB(file.size);

      if (!allowedTypes.includes(file.type)) {
        errors.push(`‚ùå Unsupported: ${file.name}`);
        return;
      }
      if (sizeMb > MAX_FILE_MB) {
        errors.push(`‚ùå Too big: ${file.name}`);
        return;
      }

      valid.push(file);
      previews.push({
        name: file.name,
        type: file.type,
        url: URL.createObjectURL(file),
        size: file.size,
      });
    });

    const totalMb = valid.reduce((s, f) => s + bytesToMB(f.size), 0);
    if (totalMb > MAX_TOTAL_MB) {
      errors.push(`‚ùå Total exceeds ${MAX_TOTAL_MB} MB.`);
      valid = [];
      previews.forEach((p) => URL.revokeObjectURL(p.url));
      previews = [];
    }

    setUploadError(errors.join("\n"));
    setAttachmentFiles(valid);
    setAttachmentPreviews(previews);
    setUploadProgress(valid.map(() => 0));

    if (errors.length > 0 && fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  }

  const handleFileChange = (e) => {
    processFiles(Array.from(e.target.files || []));
  };

  const clearAttachments = () => {
    attachmentPreviews.forEach((p) => URL.revokeObjectURL(p.url));
    setAttachmentFiles([]);
    setAttachmentPreviews([]);
    setUploadProgress([]);
    setUploadError("");
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  // Drag & drop
  const handleDragEnter = (e) => {
    e.preventDefault();
    dragCounterRef.current++;
    setIsDragging(true);
  };
  const handleDragLeave = (e) => {
    e.preventDefault();
    dragCounterRef.current--;
    if (dragCounterRef.current <= 0) setIsDragging(false);
  };
  const handleDragOver = (e) => e.preventDefault();
  const handleDrop = (e) => {
    e.preventDefault();
    dragCounterRef.current = 0;
    setIsDragging(false);
    processFiles(Array.from(e.dataTransfer.files || []));
  };

  // Send message
  const handleSend = async (e) => {
    e.preventDefault();

    if (!newMessage.trim() && attachmentFiles.length === 0) {
      setUploadError("Message or attachment required");
      return;
    }

    setSending(true);

    try {
      let attachments = [];

      if (attachmentFiles.length > 0) {
        attachments = await uploadChatAttachmentsWithProgress(
          threadId,
          attachmentFiles,
          (index, percent) => {
            setUploadProgress((prev) => {
              const next = [...prev];
              next[index] = percent;
              return next;
            });
          }
        );
      }

      await sendMessage(threadId, {
        text: newMessage.trim(),
        senderId: user.uid,
        senderName: user.displayName || user.email,
        attachments,
        readBy: [user.uid],
      });

      setNewMessage("");
      clearAttachments();
    } catch (err) {
      setUploadError("Failed to send message");
      console.error(err);
    }

    setSending(false);
    typingActiveRef.current = false;
    setThreadTyping(threadId, user.uid, false);
  };

  // Typing indicator
  const handleInputChange = (e) => {
    const val = e.target.value;
    setNewMessage(val);

    if (!typingActiveRef.current) {
      typingActiveRef.current = true;
      setThreadTyping(threadId, user.uid, true);
    }

    if (typingTimeoutRef.current)
      clearTimeout(typingTimeoutRef.current);

    typingTimeoutRef.current = setTimeout(() => {
      typingActiveRef.current = false;
      setThreadTyping(threadId, user.uid, false);
      typingTimeoutRef.current = null;
    }, 1800);
  };

  const handleBlur = () => {
    typingActiveRef.current = false;
    setThreadTyping(threadId, user.uid, false);
  };

  // Build image list
  const imageAttachments = [];
  messages.forEach((m) => {
    (m.attachments || []).forEach((att) => {
      if ((att.contentType || "").startsWith("image/")) {
        imageAttachments.push(att);
      }
    });
  });

  // Keyboard navigation
  const handleKeyDown = useCallback(
    (e) => {
      if (!lightboxOpen || !imageAttachments.length) return;

      if (e.key === "Escape") setLightboxOpen(false);
      if (e.key === "ArrowRight") {
        setLightboxIndex((i) => (i + 1) % imageAttachments.length);
        setZoom(1);
      }
      if (e.key === "ArrowLeft") {
        setLightboxIndex(
          (i) => (i - 1 + imageAttachments.length) %
            imageAttachments.length
        );
        setZoom(1);
      }
    },
    [lightboxOpen, imageAttachments.length]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () =>
      document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // SWIPE
  const handleTouchStart = (e) => {
    if (!lightboxOpen) return;
    const x = e.touches[0].clientX;
    touchStartXRef.current = x;
    touchEndXRef.current = x;
  };

  const handleTouchMove = (e) => {
    if (!lightboxOpen) return;
    touchEndXRef.current = e.touches[0].clientX;
  };

  const handleTouchEnd = () => {
    if (!lightboxOpen) return;

    const delta = touchEndXRef.current - touchStartXRef.current;
    if (Math.abs(delta) < SWIPE_THRESHOLD) return;

    if (delta < 0) {
      setLightboxIndex((i) => (i + 1) % imageAttachments.length);
    } else {
      setLightboxIndex(
        (i) =>
          (i - 1 + imageAttachments.length) %
          imageAttachments.length
      );
    }
    setZoom(1);
  };

  // ‚≠ê DOUBLE-TAP / DOUBLE-CLICK ZOOM
  const handleDoubleTap = () => {
    const now = Date.now();
    const delta = now - lastTapRef.current;
    lastTapRef.current = now;

    if (delta < DOUBLE_TAP_THRESHOLD) {
      // Toggle zoom 1 ‚áÑ 2
      setZoom((z) => (z === 1 ? 2 : 1));
    }
  };

  // Loading
  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10 text-gray-600">
        Loading conversation‚Ä¶
      </div>
    );
  }

  if (!thread) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        Thread not found.
      </div>
    );
  }

  const otherParticipants =
    (thread.participants || []).filter((u) => u !== user?.uid) ||
    [];

  return (
    <div className="max-w-3xl mx-auto px-4 py-10 flex flex-col gap-4">

      {/* HEADER */}
      <div className="flex justify-between">
        <Link
          to="/messages"
          className="text-blue-600 text-xs hover:underline"
        >
          ‚Üê Back
        </Link>
        <h1 className="text-xl font-semibold text-slate-800">
          Conversation
        </h1>
      </div>

      {/* MESSAGES */}
      <div className="bg-white rounded-xl shadow p-4 max-h-[60vh] overflow-y-auto space-y-4">
        {messages.map((m) => {
          const isMine = m.senderId === user.uid;
          const readBy = m.readBy || [];
          const attachments = m.attachments || [];

          const everyoneRead =
            isMine &&
            otherParticipants.every((id) => readBy.includes(id));

          return (
            <div
              key={m.id}
              className={`flex flex-col ${
                isMine ? "items-end" : "items-start"
              }`}
            >
              <div
                className={`max-w-[75%] rounded-2xl px-3 py-2 text-sm ${
                  isMine
                    ? "bg-blue-600 text-white rounded-br-sm"
                    : "bg-gray-100 text-slate-800 rounded-bl-sm"
                }`}
              >
                {!isMine && m.senderName && (
                  <div className="text-[11px] opacity-70 font-semibold mb-1">
                    {m.senderName}
                  </div>
                )}

                {m.text && <div className="mb-1">{m.text}</div>}

                {attachments.length > 0 && (
                  <div className="space-y-2 mt-1">
                    {attachments.map((att, a) => {
                      const type = att.contentType || "";

                      if (type.startsWith("image/")) {
                        const imgIndex = imageAttachments.findIndex(
                          (x) => x.url === att.url
                        );
                        return (
                          <button
                            key={a}
                            onClick={() => {
                              if (imgIndex >= 0) {
                                setLightboxIndex(imgIndex);
                                setZoom(1);
                                setLightboxOpen(true);
                              }
                            }}
                            className="focus:outline-none"
                          >
                            <img
                              src={att.url}
                              className="max-h-40 rounded-lg object-cover"
                            />
                          </button>
                        );
                      }

                      if (type.startsWith("video/")) {
                        return (
                          <video
                            key={a}
                            src={att.url}
                            className="max-h-40 rounded-lg"
                            controls
                          />
                        );
                      }

                      return (
                        <a
                          key={a}
                          href={att.url}
                          target="_blank"
                          className="underline text-xs"
                        >
                          üìé {att.name}
                        </a>
                      );
                    })}
                  </div>
                )}
              </div>

              {everyoneRead && (
                <span className="text-[11px] text-gray-400 mt-1">
                  Seen
                </span>
              )}
            </div>
          );
        })}

        {typingUsers.length > 0 && (
          <div className="text-xs text-gray-500 italic">
            {typingUsers.length === 1
              ? "Someone is typing‚Ä¶"
              : "Multiple users typing‚Ä¶"}
          </div>
        )}
      </div>

      {/* ERROR */}
      {uploadError && (
        <div className="bg-red-50 text-red-600 text-xs p-2 rounded">
          {uploadError}
        </div>
      )}

      {/* PREVIEW + PROGRESS */}
      {attachmentPreviews.length > 0 && (
        <div className="bg-white rounded-xl shadow p-3 space-y-3">
          {attachmentPreviews.map((p, idx) => (
            <div
              key={idx}
              className="flex items-center gap-2 border rounded-lg px-2 py-1 text-xs"
            >
              {p.type.startsWith("image/") && (
                <img
                  src={p.url}
                  className="h-10 w-10 object-cover rounded"
                />
              )}

              {!p.type.startsWith("image/") && !p.type.startsWith("video/") &&
                "üìé"}

              <span className="truncate">{p.name}</span>

              <div className="flex-1 bg-gray-200 rounded-full h-2 overflow-hidden">
                <div
                  className="bg-blue-600 h-full transition-all"
                  style={{ width: `${uploadProgress[idx]}%` }}
                />
              </div>

              <span className="w-10 text-right text-[10px]">
                {uploadProgress[idx]}%
              </span>
            </div>
          ))}

          <button
            onClick={clearAttachments}
            className="text-red-600 text-xs underline"
          >
            Clear attachments
          </button>
        </div>
      )}

      {/* COMPOSER */}
      <form
        onSubmit={handleSend}
        className={`bg-white rounded-xl shadow p-3 space-y-3 border-2 ${
          isDragging ? "border-blue-400 bg-blue-50/40" : "border-transparent"
        }`}
        onDragEnter={handleDragEnter}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <div className="flex items-center gap-3">
          <label className="text-blue-600 text-sm cursor-pointer">
            + Add files
            <input
              type="file"
              multiple
              className="hidden"
              ref={fileInputRef}
              onChange={handleFileChange}
            />
          </label>

          <span className="text-xs text-gray-400">
            or drag & drop here
          </span>
        </div>

        <div className="flex items-end gap-3">
          <textarea
            rows={2}
            value={newMessage}
            onChange={handleInputChange}
            onBlur={handleBlur}
            className="flex-1 border rounded-lg px-3 py-2 text-sm focus:outline-none"
            placeholder="Type your message‚Ä¶"
          />
          <button
            disabled={
              sending ||
              (!newMessage.trim() && attachmentFiles.length === 0)
            }
            className="px-5 py-2 bg-blue-600 text-white rounded-full text-sm disabled:opacity-40"
          >
            {sending ? "Sending‚Ä¶" : "Send"}
          </button>
        </div>
      </form>

      {/* LIGHTBOX */}
      {lightboxOpen && imageAttachments.length > 0 && (
        <div
          className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center"
          onClick={() => setLightboxOpen(false)}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* TOP BAR */}
            <div className="flex justify-between text-white mb-3">
              <span>
                {imageAttachments[lightboxIndex].name}{" "}
                <span className="opacity-60">
                  ({lightboxIndex + 1}/{imageAttachments.length})
                </span>
              </span>
              <button
                className="px-3 py-1 border border-white/40 rounded-full"
                onClick={() => setLightboxOpen(false)}
              >
                Close ‚úï
              </button>
            </div>

            {/* IMAGE ‚Äî swipe + double tap + zoom */}
            <div
              className="bg-black flex items-center justify-center max-h-[80vh] overflow-hidden rounded-lg"
              onTouchStart={(e) => {
                handleTouchStart(e);
              }}
              onTouchMove={handleTouchMove}
              onTouchEnd={(e) => {
                handleTouchEnd();
                handleDoubleTap();
              }}
              onDoubleClick={handleDoubleTap}
            >
              <img
                src={imageAttachments[lightboxIndex].url}
                className="max-h-[80vh] max-w-full transition-transform"
                style={{ transform: `scale(${zoom})` }}
              />
            </div>

            {/* CONTROLS */}
            <div className="flex justify-between items-center text-white mt-3">
              <div className="flex gap-2">
                <button
                  onClick={() =>
                    setLightboxIndex(
                      (i) =>
                        (i - 1 + imageAttachments.length) %
                        imageAttachments.length
                    )
                  }
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  ‚Üê Prev
                </button>
                <button
                  onClick={() =>
                    setLightboxIndex(
                      (i) => (i + 1) % imageAttachments.length
                    )
                  }
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  Next ‚Üí
                </button>
              </div>

              <div className="flex items-center gap-2">
                <button
                  onClick={() => setZoom((z) => Math.max(0.5, z - 0.25))}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  ‚àí
                </button>
                <span className="opacity-80 text-xs">
                  {(zoom * 100).toFixed(0)}%
                </span>
                <button
                  onClick={() => setZoom((z) => Math.min(3, z + 0.25))}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  +
                </button>
                <button
                  onClick={() => setZoom(1)}
                  className="px-3 py-1 border border-white/40 rounded-full"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
