import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  getThreadMessages,
  sendMessage,
} from "../firebase/messages.js";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();
  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    (async () => {
      const t = await getThread(threadId);
      const msgs = await getThreadMessages(threadId);
      setThread(t);
      setMessages(msgs);
      setLoading(false);
    })();
  }, [user, threadId]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    const msg = {
      senderId: user.uid,
      text: text.trim(),
    };

    await sendMessage(threadId, msg);
    setMessages((prev) => [...prev, { id: Date.now().toString(), ...msg }]);
    setText("");
  };

  if (!user) return <div className="p-4">Please login to view this thread.</div>;
  if (loading) return <div className="p-4">Loading thread...</div>;
  if (!thread) return <div className="p-4">Thread not found.</div>;

  return (
    <div className="p-4 max-w-3xl mx-auto flex flex-col gap-3">
      <h1 className="text-xl font-bold">
        Conversation {thread.subject ? `– ${thread.subject}` : ""}
      </h1>

      <div className="border rounded p-3 h-80 overflow-y-auto space-y-2">
        {messages.map((m) => (
          <div
            key={m.id}
            className={`max-w-[80%] px-3 py-2 rounded ${
              m.senderId === user.uid
                ? "ml-auto bg-blue-600 text-white"
                : "mr-auto bg-gray-200"
            }`}
          >
            {m.text}
          </div>
        ))}
      </div>

      <form onSubmit={handleSend} className="flex gap-2">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="flex-1 border rounded px-2 py-1"
          placeholder="Type a message..."
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-1 rounded"
        >
          Send
        </button>
      </form>
    </div>
  );
}
