import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import { getUserThreads } from "../firebase/messages.js";
import { formatTimestamp } from "../firebase/messageHelpers.js";

export default function MyMessages() {
  const { user } = useAuth();
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    (async () => {
      const data = await getUserThreads(user.uid);
      setThreads(data);
      setLoading(false);
    })();
  }, [user]);

  if (!user) return <div className="p-4">Please login to view messages.</div>;

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">My Messages</h1>

      {loading && <div>Loading threads...</div>}

      {!loading && threads.length === 0 && (
        <div>You don&apos;t have any message threads yet.</div>
      )}

      <div className="space-y-3">
        {threads.map((t) => (
          <Link
            key={t.id}
            to={`/thread/${t.id}`}
            className="block border rounded p-3 hover:shadow"
          >
            <div className="font-semibold">
              {t.subject || "Conversation"}
            </div>
            <div className="text-sm text-gray-600">
              Last message: {t.lastMessage || "—"}
            </div>
            <div className="text-xs text-gray-400 mt-1">
              Updated: {formatTimestamp(t.updatedAt)}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
