// src/pages/MyListings.jsx
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getListingsForUser,
  deleteListing,
} from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

function isNewListing(listing) {
  const created = listing?.createdAt;
  if (!created) return false;

  let ms;
  if (typeof created.toMillis === "function") {
    ms = created.toMillis();
  } else if (typeof created.seconds === "number") {
    ms = created.seconds * 1000;
  } else {
    ms = new Date(created).getTime();
  }

  const fourteenDays = 14 * 24 * 60 * 60 * 1000;
  return Date.now() - ms <= fourteenDays;
}

export default function MyListings() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) {
      setListings([]);
      setLoading(false);
      return;
    }

    (async () => {
      setLoading(true);
      setError("");
      try {
        const data = await getListingsForUser(user.uid);
        setListings(data || []);
      } catch (err) {
        console.error(err);
        setError(err?.message || "Failed to load your listings.");
      } finally {
        setLoading(false);
      }
    })();
  }, [user]);

  const handleDelete = async (id) => {
    const ok = window.confirm(
      "Are you sure you want to delete this listing? This cannot be undone."
    );
    if (!ok) return;

    try {
      await deleteListing(id);
      setListings((items) => items.filter((l) => l.id !== id));
    } catch (err) {
      console.error(err);
      alert(err?.message || "Failed to delete listing.");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-3 mb-6">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-slate-800">
            My Listings
          </h1>
          <p className="text-sm text-gray-500">
            Manage the homes you&apos;ve added to HI AWTO.
          </p>
        </div>

        <button
          onClick={() => navigate("/create-listing")}
          className="inline-flex items-center justify-center px-4 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700"
        >
          + New Listing
        </button>
      </div>

      {error && (
        <div className="mb-4 rounded bg-red-50 text-red-700 px-3 py-2 text-sm">
          {error}
        </div>
      )}

      {loading ? (
        <p className="text-gray-500 text-sm">Loading your listingsâ€¦</p>
      ) : listings.length === 0 ? (
        <div className="border border-dashed border-gray-300 rounded-xl p-6 text-center text-sm text-gray-500">
          You don&apos;t have any listings yet.
          <button
            onClick={() => navigate("/create-listing")}
            className="ml-2 text-blue-600 hover:underline"
          >
            Create your first listing.
          </button>
        </div>
      ) : (
        <div className="grid gap-4 md:grid-cols-2">
          {listings.map((listing) => {
            const image =
              Array.isArray(listing.imageUrls) && listing.imageUrls.length
                ? listing.imageUrls[0]
                : FALLBACK_IMG;

            const isNew = isNewListing(listing);

            return (
              <div
                key={listing.id}
                className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden flex flex-col"
              >
                <div className="h-40 bg-gray-100 overflow-hidden">
                  <img
                    src={image}
                    alt={listing.title || "Listing"}
                    className="w-full h-full object-cover"
                  />
                </div>

                <div className="p-4 flex-1 flex flex-col gap-3">
                  <div className="flex items-start justify-between gap-2">
                    <div>
                      <h2 className="font-semibold text-slate-800">
                        {listing.title || "Untitled listing"}
                      </h2>
                      <p className="text-xs text-gray-500 mt-0.5">
                        {listing.address || "No address provided"}
                      </p>
                    </div>

                    <div className="flex flex-col items-end gap-1">
                      {listing.featured && (
                        <span className="inline-flex px-2 py-0.5 rounded-full bg-yellow-100 text-yellow-800 text-[11px] font-semibold">
                          Featured
                        </span>
                      )}
                      {isNew && (
                        <span className="inline-flex px-2 py-0.5 rounded-full bg-green-100 text-green-700 text-[11px] font-semibold">
                          New
                        </span>
                      )}
                    </div>
                  </div>

                  <div className="text-sm">
                    <p className="font-semibold text-blue-600">
                      ${listing.price?.toLocaleString?.() || listing.price} total
                    </p>
                    <p className="text-green-600">
                      $
                      {listing.downPayment?.toLocaleString?.() ||
                        listing.downPayment}{" "}
                      down
                    </p>
                  </div>

                  <div className="mt-auto flex justify-between gap-2 pt-2 border-t border-gray-100">
                    <Link
                      to={`/listing/${listing.id}`}
                      className="text-xs sm:text-sm px-3 py-1.5 rounded-full border border-gray-200 text-slate-700 hover:bg-gray-50"
                    >
                      View
                    </Link>
                    <button
                      onClick={() =>
                        navigate(`/create-listing?id=${listing.id}`)
                      }
                      className="text-xs sm:text-sm px-3 py-1.5 rounded-full border border-blue-200 text-blue-700 hover:bg-blue-50"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(listing.id)}
                      className="text-xs sm:text-sm px-3 py-1.5 rounded-full border border-red-200 text-red-600 hover:bg-red-50"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
