// src/pages/ViewThread.jsx
import React, { useEffect, useState, useRef } from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  sendMessage,
  setThreadTyping,
  markThreadMessagesRead,
} from "../firebase/messages.js";
import { uploadChatAttachments } from "../firebase/uploadManager.js";
import { db } from "../firebase/config.js";
import {
  doc,
  onSnapshot,
  collection,
  query,
  orderBy,
} from "firebase/firestore";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  const [newMessage, setNewMessage] = useState("");

  // Attachments
  const [attachmentFiles, setAttachmentFiles] = useState([]);
  const [attachmentPreviews, setAttachmentPreviews] = useState([]);
  const [uploadError, setUploadError] = useState("");
  const fileInputRef = useRef(null);

  // Typing
  const [typingUsers, setTypingUsers] = useState([]);
  const typingActiveRef = useRef(false);
  const typingTimeoutRef = useRef(null);

  // Allowed attachments
  const allowedTypes = [
    "image/jpeg",
    "image/png",
    "image/webp",
    "image/gif",
    "video/mp4",
    "video/quicktime",
    "application/pdf",
    "application/msword",
    "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
  ];

  const MAX_FILE_MB = 25;         // per file
  const MAX_TOTAL_MB = 50;        // all files combined

  const bytesToMB = (bytes) => bytes / (1024 * 1024);

  // Load thread details once
  useEffect(() => {
    if (!threadId) return;

    (async () => {
      const t = await getThread(threadId);
      setThread(t || null);
      setLoading(false);
    })();
  }, [threadId]);

  // Real-time messages
  useEffect(() => {
    if (!threadId) return;

    const msgsRef = collection(db, `threads/${threadId}/messages`);
    const q = query(msgsRef, orderBy("createdAt", "asc"));

    const unsub = onSnapshot(q, (snap) => {
      setMessages(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });

    return unsub;
  }, [threadId]);

  // Real-time typing
  useEffect(() => {
    if (!threadId || !user) return;

    const ref = doc(db, "threads", threadId);

    const unsub = onSnapshot(ref, (snap) => {
      if (!snap.exists()) return;
      const data = snap.data();
      setThread({ id: snap.id, ...data });

      const typingMap = data.typing || {};
      setTypingUsers(
        Object.entries(typingMap)
          .filter(([uid, isOn]) => uid !== user.uid && isOn)
          .map(([uid]) => uid)
      );
    });

    return unsub;
  }, [threadId, user]);

  // Read receipts
  useEffect(() => {
    if (!user || !threadId || messages.length === 0) return;
    markThreadMessagesRead(threadId, user.uid).catch(console.error);
  }, [messages, threadId, user]);

  // File validation + preview
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files || []);
    let previews = [];
    let validFiles = [];
    let errors = [];

    // Validate each file
    files.forEach((file) => {
      const fileMB = bytesToMB(file.size);

      if (!allowedTypes.includes(file.type)) {
        errors.push(`‚ùå Unsupported file: ${file.name}`);
        return;
      }

      if (fileMB > MAX_FILE_MB) {
        errors.push(`‚ùå ${file.name} exceeds ${MAX_FILE_MB}MB limit`);
        return;
      }

      validFiles.push(file);
      previews.push({
        name: file.name,
        type: file.type,
        url: URL.createObjectURL(file),
        size: file.size,
      });
    });

    // Check total size
    const totalMB = validFiles.reduce(
      (sum, file) => sum + bytesToMB(file.size),
      0
    );

    if (totalMB > MAX_TOTAL_MB) {
      errors.push(
        `‚ùå Total upload size ${totalMB.toFixed(
          1
        )}MB exceeds ${MAX_TOTAL_MB}MB limit`
      );
      validFiles = [];
      previews.forEach((p) => URL.revokeObjectURL(p.url));
      previews = [];
    }

    setUploadError(errors.join("\n"));
    setAttachmentFiles(validFiles);
    setAttachmentPreviews(previews);

    if (errors.length > 0 && fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const clearAttachments = () => {
    attachmentPreviews.forEach((p) => URL.revokeObjectURL(p.url));
    setAttachmentPreviews([]);
    setAttachmentFiles([]);
    setUploadError("");
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!user || !threadId) return;

    const hasText = newMessage.trim().length > 0;
    const hasFiles = attachmentFiles.length > 0;

    if (!hasText && !hasFiles) {
      setUploadError("Message or attachment required");
      return;
    }

    setSending(true);
    setUploadError("");

    try {
      let attachments = [];

      if (attachmentFiles.length > 0) {
        attachments = await uploadChatAttachments(threadId, attachmentFiles);
      }

      await sendMessage(threadId, {
        text: newMessage.trim(),
        senderId: user.uid,
        senderName: user.displayName || user.email || "User",
        attachments,
        readBy: [user.uid],
      });

      setNewMessage("");
      clearAttachments();
    } catch {
      setUploadError("Failed to upload files");
    }

    setSending(false);

    // Stop typing
    setThreadTyping(threadId, user.uid, false);
    typingActiveRef.current = false;
  };

  // Typing indicator
  const handleInputChange = (e) => {
    const value = e.target.value;
    setNewMessage(value);

    if (!user || !threadId) return;

    if (!typingActiveRef.current) {
      typingActiveRef.current = true;
      setThreadTyping(threadId, user.uid, true);
    }

    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    typingTimeoutRef.current = setTimeout(() => {
      typingActiveRef.current = false;
      setThreadTyping(threadId, user.uid, false);
      typingTimeoutRef.current = null;
    }, 2000);
  };

  const handleBlur = () => {
    typingActiveRef.current = false;
    setThreadTyping(threadId, user.uid, false);
  };

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Loading conversation‚Ä¶</p>
      </div>
    );
  }

  if (!thread) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500 text-sm">Thread not found.</p>
        <Link to="/messages" className="text-blue-600 text-sm">‚Üê Back</Link>
      </div>
    );
  }

  const otherParticipants =
    (thread.participants || []).filter((id) => id !== user?.uid) ?? [];

  return (
    <div className="max-w-3xl mx-auto px-4 py-10 flex flex-col gap-4">
      <div className="flex justify-between items-center">
        <Link
          to="/messages"
          className="text-blue-600 text-xs hover:underline"
        >
          ‚Üê Back to messages
        </Link>
        <h1 className="text-xl font-semibold text-slate-800">
          Conversation
        </h1>
      </div>

      {/* MESSAGES */}

      <div className="bg-white rounded-xl shadow p-4 max-h-[60vh] overflow-y-auto space-y-4">
        {messages.map((m) => {
          const isMine = m.senderId === user?.uid;
          const readBy = Array.isArray(m.readBy) ? m.readBy : [];
          const attachments =
            Array.isArray(m.attachments) ? m.attachments : [];

          const allOthersRead =
            isMine &&
            otherParticipants.length > 0 &&
            otherParticipants.every((id) => readBy.includes(id));

          return (
            <div
              key={m.id}
              className={`flex flex-col ${
                isMine ? "items-end" : "items-start"
              }`}
            >
              <div
                className={`max-w-[75%] rounded-2xl px-3 py-2 text-sm ${
                  isMine
                    ? "bg-blue-600 text-white rounded-br-sm"
                    : "bg-gray-100 text-slate-800 rounded-bl-sm"
                }`}
              >
                {!isMine && m.senderName && (
                  <div className="text-[11px] font-semibold mb-0.5 opacity-80">
                    {m.senderName}
                  </div>
                )}

                {m.text && <div className="mb-1">{m.text}</div>}

                {attachments.length > 0 && (
                  <div className="space-y-2 mt-1">
                    {attachments.map((att, idx) => {
                      const type = att.contentType || "";
                      if (type.startsWith("image/")) {
                        return (
                          <a
                            key={idx}
                            href={att.url}
                            target="_blank"
                            rel="noreferrer"
                          >
                            <img
                              src={att.url}
                              alt={att.name}
                              className="max-h-40 rounded-lg object-cover"
                            />
                          </a>
                        );
                      }
                      if (type.startsWith("video/")) {
                        return (
                          <video
                            key={idx}
                            controls
                            className="max-h-40 rounded-lg"
                            src={att.url}
                          />
                        );
                      }
                      return (
                        <a
                          key={idx}
                          href={att.url}
                          target="_blank"
                          rel="noreferrer"
                          className="text-xs underline flex items-center gap-1"
                        >
                          üìé {att.name}
                        </a>
                      );
                    })}
                  </div>
                )}
              </div>

              {allOthersRead && (
                <span className="text-[11px] text-gray-400 mt-1">
                  Seen
                </span>
              )}
            </div>
          );
        })}

        {typingUsers.length > 0 && (
          <div className="text-xs text-gray-500 italic">
            {typingUsers.length === 1
              ? "Someone is typing‚Ä¶"
              : "Several people are typing‚Ä¶"}
          </div>
        )}
      </div>

      {/* ERROR MESSAGE */}
      {uploadError && (
        <div className="bg-red-50 text-red-600 text-xs rounded-lg px-3 py-2 whitespace-pre-line">
          {uploadError}
        </div>
      )}

      {/* ATTACHMENT PREVIEWS */}
      {attachmentPreviews.length > 0 && (
        <div className="bg-white rounded-xl shadow p-3 flex flex-wrap gap-3 items-center">
          {attachmentPreviews.map((p, idx) => {
            const isImage = p.type.startsWith("image/");
            const isVideo = p.type.startsWith("video/");

            return (
              <div
                key={idx}
                className="flex items-center gap-2 border rounded-lg px-2 py-1 text-xs"
              >
                {isImage && (
                  <img
                    src={p.url}
                    alt={p.name}
                    className="h-10 w-10 rounded object-cover"
                  />
                )}
                {isVideo && <span>üé¨</span>}
                {!isImage && !isVideo && <span>üìé</span>}
                <span className="max-w-[120px] truncate">{p.name}</span>
              </div>
            );
          })}

          <button
            type="button"
            onClick={clearAttachments}
            className="ml-auto text-xs text-red-600 hover:underline"
          >
            Clear
          </button>
        </div>
      )}

      {/* COMPOSER */}
      <form
        onSubmit={handleSend}
        className="bg-white rounded-xl shadow p-3 flex flex-col gap-3"
      >
        <div>
          <label className="cursor-pointer text-sm text-blue-600 hover:underline">
            + Add files
            <input
              ref={fileInputRef}
              type="file"
              multiple
              className="hidden"
              onChange={handleFileChange}
            />
          </label>
        </div>

        <div className="flex items-end gap-3">
          <textarea
            rows={2}
            value={newMessage}
            onChange={handleInputChange}
            onBlur={handleBlur}
            placeholder="Type your message‚Ä¶"
            className="flex-1 border rounded-lg px-3 py-2 text-sm resize-none focus:outline-none focus:ring-blue-500/40 focus:border-blue-500"
          />

          <button
            type="submit"
            disabled={
              sending ||
              (!newMessage.trim() && attachmentFiles.length === 0)
            }
            className="px-5 py-2 bg-blue-600 text-white rounded-full text-sm hover:bg-blue-700 disabled:opacity-50"
          >
            {sending ? "Sending‚Ä¶" : "Send"}
          </button>
        </div>
      </form>
    </div>
  );
}
