// src/pages/ViewThread.jsx
import React, { useEffect, useState, useRef } from "react";
import { useParams, Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getThread,
  sendMessage,
  setThreadTyping,
  markThreadMessagesRead,
} from "../firebase/messages.js";
import { uploadChatAttachmentsWithProgress } from "../firebase/uploadManager.js";
import { db } from "../firebase/config.js";
import {
  doc,
  onSnapshot,
  collection,
  query,
  orderBy,
} from "firebase/firestore";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [thread, setThread] = useState(null);
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  const [newMessage, setNewMessage] = useState("");

  // Attachments & progress
  const [attachmentFiles, setAttachmentFiles] = useState([]);
  const [attachmentPreviews, setAttachmentPreviews] = useState([]);
  const [uploadError, setUploadError] = useState("");
  const [uploadProgress, setUploadProgress] = useState([]);
  const fileInputRef = useRef(null);

  // Drag & Drop
  const [isDragging, setIsDragging] = useState(false);
  const dragCounterRef = useRef(0);

  // Typing
  const [typingUsers, setTypingUsers] = useState([]);
  const typingActiveRef = useRef(false);
  const typingTimeoutRef = useRef(null);

  // Lightbox
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  // Allowed Files
  const allowedTypes = [
    "image/jpeg",
    "image/png",
    "image/webp",
    "image/gif",
    "video/mp4",
    "video/quicktime",
    "application/pdf",
    "application/msword",
    "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
  ];
  const MAX_FILE_MB = 25;
  const MAX_TOTAL_MB = 50;
  const bytesToMB = (b) => b / (1024 * 1024);

  // -------- Load Thread Meta --------
  useEffect(() => {
    if (!threadId) return;
    (async () => {
      const t = await getThread(threadId);
      setThread(t || null);
      setLoading(false);
    })();
  }, [threadId]);

  // -------- Real-time Messages --------
  useEffect(() => {
    if (!threadId) return;

    const msgsRef = collection(db, `threads/${threadId}/messages`);
    const q = query(msgsRef, orderBy("createdAt", "asc"));

    const unsub = onSnapshot(q, (snap) => {
      setMessages(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
    });

    return unsub;
  }, [threadId]);

  // -------- Real-time Typing --------
  useEffect(() => {
    if (!threadId || !user) return;

    const ref = doc(db, "threads", threadId);

    const unsub = onSnapshot(ref, (snap) => {
      if (!snap.exists()) return;

      const data = snap.data();
      setThread({ id: snap.id, ...data });

      const typingMap = data.typing || {};
      setTypingUsers(
        Object.entries(typingMap)
          .filter(([uid, isOn]) => uid !== user.uid && isOn)
          .map(([uid]) => uid)
      );
    });

    return unsub;
  }, [threadId, user]);

  // -------- Mark Messages Read --------
  useEffect(() => {
    if (!user || !threadId || messages.length === 0) return;
    markThreadMessagesRead(threadId, user.uid).catch(console.error);
  }, [messages, threadId, user]);

  // -------- File Helpers --------
  function processFiles(files) {
    let valid = [];
    let previews = [];
    let errors = [];

    files.forEach((file) => {
      const fileMB = bytesToMB(file.size);

      if (!allowedTypes.includes(file.type)) {
        errors.push(`‚ùå Unsupported: ${file.name}`);
        return;
      }
      if (fileMB > MAX_FILE_MB) {
        errors.push(`‚ùå Too large: ${file.name}`);
        return;
      }

      valid.push(file);
      previews.push({
        name: file.name,
        type: file.type,
        size: file.size,
        url: URL.createObjectURL(file),
      });
    });

    const total = valid.reduce((sum, f) => sum + bytesToMB(f.size), 0);
    if (total > MAX_TOTAL_MB) {
      errors.push(`‚ùå Total exceeds ${MAX_TOTAL_MB}MB`);
      valid = [];
      previews.forEach((p) => URL.revokeObjectURL(p.url));
      previews = [];
    }

    setUploadError(errors.join("\n"));
    setAttachmentFiles(valid);
    setAttachmentPreviews(previews);
    setUploadProgress(valid.map(() => 0));

    if (errors.length > 0 && fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  }

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files || []);
    processFiles(files);
  };

  const clearAttachments = () => {
    attachmentPreviews.forEach((p) => URL.revokeObjectURL(p.url));
    setAttachmentFiles([]);
    setAttachmentPreviews([]);
    setUploadProgress([]);
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  // -------- Drag & Drop --------
  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dragCounterRef.current++;
    setIsDragging(true);
  };
  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dragCounterRef.current--;
    if (dragCounterRef.current <= 0) setIsDragging(false);
  };
  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dragCounterRef.current = 0;
    setIsDragging(false);
    const files = Array.from(e.dataTransfer.files || []);
    processFiles(files);
  };

  // -------- Send Message --------
  const handleSend = async (e) => {
    e.preventDefault();
    if (!user || !threadId) return;

    const hasText = newMessage.trim().length > 0;
    const hasFiles = attachmentFiles.length > 0;

    if (!hasText && !hasFiles) {
      setUploadError("Message or attachment required");
      return;
    }

    setSending(true);
    setUploadError("");

    try {
      let attachments = [];

      if (attachmentFiles.length > 0) {
        attachments = await uploadChatAttachmentsWithProgress(
          threadId,
          attachmentFiles,
          (fileIndex, percent) => {
            setUploadProgress((prev) => {
              const copy = [...prev];
              copy[fileIndex] = percent;
              return copy;
            });
          }
        );
      }

      await sendMessage(threadId, {
        text: newMessage.trim(),
        senderId: user.uid,
        senderName: user.displayName || user.email || "User",
        attachments,
        readBy: [user.uid],
      });

      setNewMessage("");
      clearAttachments();
    } catch (err) {
      console.error(err);
      setUploadError("Failed to send attachments");
    }

    setSending(false);
    setThreadTyping(threadId, user.uid, false);
    typingActiveRef.current = false;
  };

  // -------- Typing --------
  const handleInputChange = (e) => {
    const v = e.target.value;
    setNewMessage(v);

    if (!user || !threadId) return;

    if (!typingActiveRef.current) {
      typingActiveRef.current = true;
      setThreadTyping(threadId, user.uid, true).catch(console.error);
    }

    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    typingTimeoutRef.current = setTimeout(() => {
      typingActiveRef.current = false;
      setThreadTyping(threadId, user.uid, false).catch(console.error);
      typingTimeoutRef.current = null;
    }, 2000);
  };

  const handleBlur = () => {
    typingActiveRef.current = false;
    if (!user || !threadId) return;
    setThreadTyping(threadId, user.uid, false).catch(console.error);
  };

  // -------- Lightbox Helpers --------
  const openLightboxAt = (index) => {
    if (index < 0 || index >= imageAttachments.length) return;
    setLightboxIndex(index);
    setZoom(1);
    setLightboxOpen(true);
  };

  const closeLightbox = () => {
    setLightboxOpen(false);
  };

  const nextImage = () => {
    if (imageAttachments.length === 0) return;
    setLightboxIndex((prev) => (prev + 1) % imageAttachments.length);
    setZoom(1);
  };

  const prevImage = () => {
    if (imageAttachments.length === 0) return;
    setLightboxIndex((prev) =>
      (prev - 1 + imageAttachments.length) % imageAttachments.length
    );
    setZoom(1);
  };

  const zoomIn = () => setZoom((z) => Math.min(z + 0.25, 3));
  const zoomOut = () => setZoom((z) => Math.max(z - 0.25, 0.5));
  const resetZoom = () => setZoom(1);

  // -------- Render Skeleton --------
  if (loading) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading conversation‚Ä¶</p>
      </div>
    );
  }

  if (!thread) {
    return (
      <div className="max-w-3xl mx-auto px-4 py-10">
        <p className="text-gray-500">Thread not found.</p>
        <Link to="/messages" className="text-blue-600 text-sm hover:underline">
          ‚Üê Back
        </Link>
      </div>
    );
  }

  const otherParticipants =
    (thread.participants || []).filter((id) => id !== user?.uid) ?? [];

  // Build a flat list of all image attachments in this thread for the lightbox
  const imageAttachments = [];
  messages.forEach((m) => {
    const atts = Array.isArray(m.attachments) ? m.attachments : [];
    atts.forEach((att) => {
      const type = att.contentType || "";
      if (type.startsWith("image/")) {
        imageAttachments.push({
          url: att.url,
          name: att.name || "Image",
        });
      }
    });
  });

  return (
    <div className="max-w-3xl mx-auto px-4 py-10 flex flex-col gap-4">
      <div className="flex justify-between items-center">
        <Link
          to="/messages"
          className="text-blue-600 text-xs hover:underline"
        >
          ‚Üê Back to messages
        </Link>
        <h1 className="text-xl font-semibold text-slate-800">
          Conversation
        </h1>
      </div>

      {/* ---------- MESSAGES ---------- */}
      <div className="bg-white rounded-xl shadow p-4 max-h-[60vh] overflow-y-auto space-y-4">
        {messages.map((m) => {
          const isMine = m.senderId === user?.uid;
          const readBy = Array.isArray(m.readBy) ? m.readBy : [];
          const attachments = Array.isArray(m.attachments)
            ? m.attachments
            : [];

          const allOthersRead =
            isMine &&
            otherParticipants.length > 0 &&
            otherParticipants.every((id) => readBy.includes(id));

          return (
            <div
              key={m.id}
              className={`flex flex-col ${
                isMine ? "items-end" : "items-start"
              }`}
            >
              <div
                className={`max-w-[75%] rounded-2xl px-3 py-2 text-sm ${
                  isMine
                    ? "bg-blue-600 text-white rounded-br-sm"
                    : "bg-gray-100 text-slate-800 rounded-bl-sm"
                }`}
              >
                {!isMine && m.senderName && (
                  <div className="text-[11px] font-semibold mb-0.5 opacity-80">
                    {m.senderName}
                  </div>
                )}

                {m.text && (
                  <div className="mb-1 whitespace-pre-line">{m.text}</div>
                )}

                {/* Attachments */}
                {attachments.length > 0 && (
                  <div className="space-y-2 mt-1">
                    {attachments.map((att, idx) => {
                      const type = att.contentType || "";

                      if (type.startsWith("image/")) {
                        // Find index of this image in flat list for lightbox
                        const imgIndex = imageAttachments.findIndex(
                          (img) =>
                            img.url === att.url &&
                            img.name === (att.name || "Image")
                        );

                        return (
                          <button
                            key={idx}
                            type="button"
                            onClick={() =>
                              imgIndex >= 0 && openLightboxAt(imgIndex)
                            }
                            className="block rounded-lg overflow-hidden focus:outline-none focus:ring-2 focus:ring-white/60"
                          >
                            <img
                              src={att.url}
                              alt={att.name || "Image attachment"}
                              className="max-h-40 rounded-lg object-cover"
                            />
                          </button>
                        );
                      }

                      if (type.startsWith("video/")) {
                        return (
                          <video
                            key={idx}
                            src={att.url}
                            controls
                            className="max-h-40 rounded-lg"
                          />
                        );
                      }

                      return (
                        <a
                          key={idx}
                          href={att.url}
                          target="_blank"
                          rel="noreferrer"
                          className="text-xs underline flex items-center gap-1"
                        >
                          üìé {att.name || "Attachment"}
                        </a>
                      );
                    })}
                  </div>
                )}
              </div>

              {allOthersRead && (
                <span className="text-[11px] text-gray-400 mt-1">
                  Seen
                </span>
              )}
            </div>
          );
        })}

        {/* Typing indicator */}
        {typingUsers.length > 0 && (
          <div className="text-xs text-gray-500 italic">
            {typingUsers.length === 1
              ? "Someone is typing‚Ä¶"
              : "Several people are typing‚Ä¶"}
          </div>
        )}
      </div>

      {/* ---------- UPLOAD ERROR ---------- */}
      {uploadError && (
        <div className="bg-red-50 text-red-600 text-xs rounded-lg px-3 py-2 whitespace-pre-line">
          {uploadError}
        </div>
      )}

      {/* ---------- ATTACHMENT PREVIEWS + PROGRESS ---------- */}
      {attachmentPreviews.length > 0 && (
        <div className="bg-white rounded-xl shadow p-3 flex flex-col gap-3">
          {attachmentPreviews.map((p, idx) => {
            const isImage = p.type.startsWith("image/");
            const isVideo = p.type.startsWith("video/");

            return (
              <div
                key={idx}
                className="flex items-center gap-2 border rounded-lg px-2 py-1 text-xs"
              >
                {isImage && (
                  <img
                    src={p.url}
                    alt={p.name}
                    className="h-10 w-10 rounded object-cover"
                  />
                )}
                {isVideo && <span>üé¨</span>}
                {!isImage && !isVideo && <span>üìé</span>}

                <span className="max-w-[150px] truncate">{p.name}</span>

                <div className="flex-1 h-2 bg-gray-200 rounded-full overflow-hidden ml-3">
                  <div
                    className="h-full bg-blue-600 transition-all"
                    style={{ width: `${uploadProgress[idx] || 0}%` }}
                  />
                </div>

                <span className="text-[10px] text-gray-500 w-10 text-right">
                  {uploadProgress[idx] || 0}%
                </span>
              </div>
            );
          })}

          <button
            type="button"
            onClick={clearAttachments}
            className="text-red-600 text-xs underline self-end"
          >
            Clear attachments
          </button>
        </div>
      )}

      {/* ---------- COMPOSER + DRAG & DROP ---------- */}
      <form
        onSubmit={handleSend}
        className={`
          bg-white rounded-xl shadow p-3 flex flex-col gap-3
          border-2 transition
          ${isDragging ? "border-blue-400 bg-blue-50/40 border-dashed" : "border-transparent"}
        `}
        onDragEnter={handleDragEnter}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <div className="flex items-center gap-3">
          <label className="cursor-pointer text-sm text-blue-600 hover:underline">
            + Add files
            <input
              ref={fileInputRef}
              type="file"
              multiple
              className="hidden"
              onChange={handleFileChange}
            />
          </label>

          <span className="text-xs text-gray-400">
            or drag & drop files here
          </span>
        </div>

        <div className="flex items-end gap-3">
          <textarea
            rows={2}
            value={newMessage}
            onChange={handleInputChange}
            onBlur={handleBlur}
            placeholder={
              isDragging ? "Drop files to attach‚Ä¶" : "Type your message‚Ä¶"
            }
            className="flex-1 border rounded-lg px-3 py-2 text-sm resize-none focus:outline-none focus:ring-blue-500/40 focus:border-blue-500"
          />

          <button
            type="submit"
            disabled={
              sending ||
              (!newMessage.trim() && attachmentFiles.length === 0)
            }
            className="px-5 py-2 rounded-full bg-blue-600 text-white text-sm hover:bg-blue-700 disabled:opacity-50"
          >
            {sending ? "Sending‚Ä¶" : "Send"}
          </button>
        </div>
      </form>

      {/* ---------- LIGHTBOX OVERLAY ---------- */}
      {lightboxOpen && imageAttachments.length > 0 && (
        <div
          className="fixed inset-0 bg-black/80 flex items-center justify-center z-50"
          onClick={closeLightbox}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Top bar */}
            <div className="flex items-center justify-between mb-3 text-white text-sm">
              <div>
                {imageAttachments[lightboxIndex]?.name || "Image"}{" "}
                <span className="opacity-70">
                  ({lightboxIndex + 1} / {imageAttachments.length})
                </span>
              </div>
              <button
                onClick={closeLightbox}
                className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
              >
                Close ‚úï
              </button>
            </div>

            {/* Image container */}
            <div className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]">
              <div className="overflow-auto flex items-center justify-center max-h-[80vh] max-w-full">
                <img
                  src={imageAttachments[lightboxIndex].url}
                  alt={imageAttachments[lightboxIndex].name}
                  className="max-h-[80vh] max-w-full transition-transform duration-150"
                  style={{ transform: `scale(${zoom})` }}
                />
              </div>
            </div>

            {/* Controls */}
            <div className="flex items-center justify-between mt-3 text-white text-sm">
              {/* Left controls: prev / next */}
              <div className="flex items-center gap-2">
                <button
                  onClick={prevImage}
                  className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
                >
                  ‚Üê Prev
                </button>
                <button
                  onClick={nextImage}
                  className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
                >
                  Next ‚Üí
                </button>
              </div>

              {/* Right controls: zoom */}
              <div className="flex items-center gap-2">
                <button
                  onClick={zoomOut}
                  className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
                >
                  ‚àí
                </button>
                <span className="text-xs opacity-80">
                  {(zoom * 100).toFixed(0)}%
                </span>
                <button
                  onClick={zoomIn}
                  className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
                >
                  +
                </button>
                <button
                  onClick={resetZoom}
                  className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
