import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { collection, getDocs, orderBy, query } from "firebase/firestore";
import { db } from "../firebase/config.js";

export default function MessagingCenterAdmin() {
  const [threads, setThreads] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      const q = query(
        collection(db, "threads"),
        orderBy("updatedAt", "desc")
      );
      const snap = await getDocs(q);
      setThreads(snap.docs.map((d) => ({ id: d.id, ...d.data() })));
      setLoading(false);
    })();
  }, []);

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Admin Messaging Center</h1>

      {loading && <div>Loading threads...</div>}

      {!loading && threads.length === 0 && (
        <div>No threads in the system yet.</div>
      )}

      <div className="space-y-3">
        {threads.map((t) => (
          <div
            key={t.id}
            className="border rounded p-3 flex justify-between items-center"
          >
            <div>
              <div className="font-semibold">{t.subject || "Conversation"}</div>
              <div className="text-xs text-gray-500">
                Participants: {t.participants?.join(", ") || "n/a"}
              </div>
            </div>
            <Link
              to={`/thread/${t.id}`}
              className="text-blue-600 text-sm hover:underline"
            >
              Open
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}
