import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { getThreadMessages, sendMessage } from "../firebase/messages.js";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function ViewThread() {
  const { threadId } = useParams();
  const { user } = useAuth();

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const bottomRef = useRef(null);

  useEffect(() => {
    (async () => {
      const msgs = await getThreadMessages(threadId);
      setMessages(msgs);
      setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 100);
    })();
  }, [threadId]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    await sendMessage(threadId, user.uid, input);
    setInput("");
    const msgs = await getThreadMessages(threadId);
    setMessages(msgs);

    setTimeout(() => bottomRef.current?.scrollIntoView({ behavior: "smooth" }), 50);
  };

  return (
    <div className="page fade-in">
      <h1 className="page-title">Conversation</h1>

      {/* Messages */}
      <div
        className="card"
        style={{
          padding: "16px",
          maxHeight: "60vh",
          overflowY: "auto",
          display: "flex",
          flexDirection: "column",
          gap: "12px"
        }}
      >
        {messages.map((m) => {
          const isMine = m.senderId === user.uid;
          return (
            <div
              key={m.id}
              style={{
                alignSelf: isMine ? "flex-end" : "flex-start",
                maxWidth: "75%",
                background: isMine ? "var(--blue)" : "#222",
                color: isMine ? "#000" : "#fff",
                padding: "10px 14px",
                borderRadius: "10px",
                boxShadow: isMine
                  ? "0 0 10px var(--blue)"
                  : "0 0 8px rgba(255,255,255,0.1)"
              }}
            >
              {m.text}
              <div style={{ fontSize: "10px", opacity: 0.6, marginTop: "4px" }}>
                {new Date(m.createdAt).toLocaleString()}
              </div>
            </div>
          );
        })}

        <div ref={bottomRef}></div>
      </div>

      {/* Send Message */}
      <form onSubmit={handleSend} style={{ marginTop: "14px", display: "flex", gap: "8px" }}>
        <input
          className="input"
          placeholder="Type a messageâ€¦"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          style={{ flexGrow: 1 }}
        />

        <button className="btn btn-primary" style={{ padding: "0 18px" }}>
          Send
        </button>
      </form>
    </div>
  );
}
