// src/pages/ListingDetails.jsx
import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { getListingById } from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

function isNewListing(listing, days = 14) {
  if (!listing?.createdAt) return false;
  try {
    let created;
    if (typeof listing.createdAt.toMillis === "function") {
      created = new Date(listing.createdAt.toMillis());
    } else {
      created = new Date(listing.createdAt);
    }
    if (!created || Number.isNaN(created.getTime())) return false;
    const diffMs = Date.now() - created.getTime();
    const diffDays = diffMs / (1000 * 60 * 60 * 24);
    return diffDays <= days;
  } catch {
    return false;
  }
}

export default function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);
  const [activeIndex, setActiveIndex] = useState(0);
  const [lightboxOpen, setLightboxOpen] = useState(false);

  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        const data = await getListingById(id);
        setListing(data || null);
      } finally {
        setLoading(false);
      }
    })();
  }, [id]);

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading listing…</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Listing not found.</p>
        <Link
          to="/"
          className="text-blue-600 hover:underline mt-2 inline-block"
        >
          ← Back to listings
        </Link>
      </div>
    );
  }

  const images =
    Array.isArray(listing.imageUrls) && listing.imageUrls.length
      ? listing.imageUrls
      : [FALLBACK_IMG];

  const activeImage = images[activeIndex] || images[0];
  const isNew = isNewListing(listing);

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <Link
        to="/"
        className="text-blue-600 text-sm hover:underline inline-block mb-4"
      >
        ← Back to listings
      </Link>

      <div className="grid lg:grid-cols-3 gap-10">
        {/* LEFT: IMAGES */}
        <div className="lg:col-span-2 space-y-4">
          {/* Main image */}
          <div
            className="h-72 md:h-96 bg-gray-200 rounded-xl overflow-hidden group cursor-zoom-in"
            onClick={() => setLightboxOpen(true)}
          >
            <img
              src={activeImage}
              alt={listing.title || "Listing"}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform"
            />
          </div>

          {/* Thumbnails */}
          {images.length > 1 && (
            <div className="flex gap-2 overflow-x-auto pb-1">
              {images.map((img, idx) => (
                <button
                  key={idx}
                  type="button"
                  onClick={() => setActiveIndex(idx)}
                  className={`h-16 w-24 rounded-lg overflow-hidden border ${
                    idx === activeIndex
                      ? "border-blue-500"
                      : "border-transparent opacity-70 hover:opacity-100"
                  }`}
                >
                  <img
                    src={img}
                    alt={`Thumbnail ${idx + 1}`}
                    className="w-full h-full object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT: DETAILS */}
        <div className="space-y-4">
          <div>
            <div className="flex items-start justify-between gap-3">
              <div>
                <h1 className="text-2xl md:text-3xl font-bold text-slate-800">
                  {listing.title || "Untitled Listing"}
                </h1>
                <p className="text-gray-600 mt-1">
                  {listing.address || "No address provided"}
                </p>
              </div>

              {/* Badges */}
              <div className="flex flex-col items-end gap-1">
                {listing.featured && (
                  <span className="px-2 py-0.5 rounded-full bg-yellow-100 text-yellow-800 text-[11px] font-semibold">
                    FEATURED HOME
                  </span>
                )}
                {isNew && (
                  <span className="px-2 py-0.5 rounded-full bg-blue-600 text-white text-[11px] font-semibold">
                    NEW LISTING
                  </span>
                )}
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow p-4 space-y-2">
            <p className="text-blue-600 font-semibold text-xl">
              ${listing.price?.toLocaleString?.() || listing.price} total
            </p>
            <p className="text-green-600 font-medium">
              $
              {listing.downPayment?.toLocaleString?.() || listing.downPayment}{" "}
              down
            </p>
          </div>

          {listing.description && (
            <div className="bg-white rounded-xl shadow p-4">
              <h2 className="font-semibold text-slate-800 mb-2">
                About this home
              </h2>
              <p className="text-gray-700 text-sm whitespace-pre-line">
                {listing.description}
              </p>
            </div>
          )}

          {/* Simple CTA */}
          <div className="bg-blue-50 border border-blue-100 rounded-xl p-4">
            <h3 className="font-semibold text-slate-800 mb-1">
              Interested in this property?
            </h3>
            <p className="text-gray-600 text-sm mb-3">
              Log in or create a free HI AWTO account to message the team and
              start your lease-to-own application.
            </p>
            <div className="flex gap-2">
              <Link
                to="/register"
                className="px-4 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700"
              >
                Get Started
              </Link>
              <Link
                to="/login"
                className="px-4 py-2 rounded-full border border-blue-200 text-blue-700 text-sm font-semibold hover:bg-blue-50"
              >
                Log In
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* LIGHTBOX OVERLAY */}
      {lightboxOpen && (
        <div
          className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center"
          onClick={() => setLightboxOpen(false)}
        >
          <div
            className="max-w-4xl w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex justify-end mb-3">
              <button
                onClick={() => setLightboxOpen(false)}
                className="text-white text-sm px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
              >
                Close
              </button>
            </div>
            <div className="bg-black rounded-lg overflow-hidden">
              <img
                src={activeImage}
                alt={listing.title || "Listing enlarged"}
                className="w-full max-h-[80vh] object-contain"
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
