import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import { createListing } from "../firebase/listings.js";
import { uploadMultipleFiles } from "../firebase/uploadManager.js";

export default function CreateListing() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",
    address: "",
    price: "",
    downPayment: "",
    leaseTerms: "",
    description: "",
  });
  const [files, setFiles] = useState([]);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  if (!user) {
    return <div className="p-4">You must be logged in to create a listing.</div>;
  }

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files || []));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError("");

    try {
      let images = [];
      if (files.length) {
        images = await uploadMultipleFiles(files, "listing-images");
      }

      const data = {
        ...form,
        price: Number(form.price || 0),
        downPayment: Number(form.downPayment || 0),
        images: images.map((i) => i.url),
      };

      const listing = await createListing(user.uid, data);
      navigate(`/listing/${listing.id}`);
    } catch (err) {
      setError(err.message || "Failed to create listing");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Create Listing</h1>
      {error && <div className="mb-3 text-red-600">{error}</div>}

      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label className="block text-sm mb-1">Title</label>
          <input
            name="title"
            value={form.title}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
            required
          />
        </div>

        <div>
          <label className="block text-sm mb-1">Address</label>
          <input
            name="address"
            value={form.address}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
          />
        </div>

        <div className="grid grid-cols-2 gap-3">
          <div>
            <label className="block text-sm mb-1">Price</label>
            <input
              name="price"
              type="number"
              value={form.price}
              onChange={handleChange}
              className="w-full border rounded px-2 py-1"
            />
          </div>

          <div>
            <label className="block text-sm mb-1">Down Payment</label>
            <input
              name="downPayment"
              type="number"
              value={form.downPayment}
              onChange={handleChange}
              className="w-full border rounded px-2 py-1"
            />
          </div>
        </div>

        <div>
          <label className="block text-sm mb-1">Lease Terms</label>
          <input
            name="leaseTerms"
            value={form.leaseTerms}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
          />
        </div>

        <div>
          <label className="block text-sm mb-1">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="w-full border rounded px-2 py-1"
            rows={4}
          />
        </div>

        <div>
          <label className="block text-sm mb-1">Images</label>
          <input type="file" multiple onChange={handleFileChange} />
        </div>

        <button
          type="submit"
          disabled={saving}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-60"
        >
          {saving ? "Creating..." : "Create Listing"}
        </button>
      </form>
    </div>
  );
}
