import {
  collection,
  addDoc,
  doc,
  getDoc,
  getDocs,
  updateDoc,
  query,
  where,
  orderBy,
} from "firebase/firestore";
import { db } from "./config";

const THREADS_COL = "threads";

export async function getUserThreads(userId) {
  const q = query(
    collection(db, THREADS_COL),
    where("participants", "array-contains", userId),
    orderBy("updatedAt", "desc")
  );
  const snap = await getDocs(q);
  return snap.docs.map((d) => ({ id: d.id, ...d.data() }));
}

export async function getThread(threadId) {
  const ref = doc(db, THREADS_COL, threadId);
  const snap = await getDoc(ref);
  return snap.exists() ? { id: snap.id, ...snap.data() } : null;
}

export async function createThread(participants, initialMessage) {
  const now = new Date();
  const threadRef = await addDoc(collection(db, THREADS_COL), {
    participants,
    createdAt: now,
    updatedAt: now,
    lastMessage: initialMessage.text || "",
  });

  await addDoc(collection(threadRef, "messages"), {
    ...initialMessage,
    createdAt: now,
  });

  return threadRef.id;
}

export async function sendMessage(threadId, message) {
  const threadRef = doc(db, THREADS_COL, threadId);
  const now = new Date();

  await addDoc(collection(threadRef, "messages"), {
    ...message,
    createdAt: now,
  });

  await updateDoc(threadRef, {
    updatedAt: now,
    lastMessage: message.text || "",
  });
}

export async function getThreadMessages(threadId) {
  const q = query(
    collection(db, `${THREADS_COL}/${threadId}/messages`),
    orderBy("createdAt", "asc")
  );
  const snap = await getDocs(q);
  return snap.docs.map((d) => ({ id: d.id, ...d.data() }));
}
