import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import { getListingsForUser, deleteListing } from "../firebase/listings.js";

export default function MyListings() {
  const { user } = useAuth();
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    (async () => {
      const data = await getListingsForUser(user.uid);
      setListings(data);
      setLoading(false);
    })();
  }, [user]);

  if (!user) return <div className="p-4">Please login to view your listings.</div>;

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this listing?")) return;
    await deleteListing(id);
    setListings((prev) => prev.filter((l) => l.id !== id));
  };

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <div className="flex items-center justify-between mb-4">
        <h1 className="text-2xl font-bold">My Listings</h1>
        <Link to="/create-listing" className="text-blue-600">
          + New Listing
        </Link>
      </div>

      {loading && <div>Loading...</div>}

      {!loading && listings.length === 0 && (
        <div>You haven&apos;t created any listings yet.</div>
      )}

      <div className="space-y-3">
        {listings.map((l) => (
          <div key={l.id} className="border rounded p-3 flex justify-between">
            <div>
              <div className="font-semibold">{l.title}</div>
              <div className="text-sm text-gray-600">{l.address}</div>
            </div>
            <div className="flex gap-2 items-center">
              <Link
                to={`/listing/${l.id}`}
                className="text-blue-600 text-sm hover:underline"
              >
                View
              </Link>
              <button
                onClick={() => handleDelete(l.id)}
                className="text-red-600 text-sm"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
