// src/pages/ForgotPassword.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { resetPassword } from "../firebase/auth.js";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");
  const [sending, setSending] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");
    setError("");
    setSending(true);

    try {
      await resetPassword(email);
      setStatus(
        "If an account exists for that email, a reset link has been sent."
      );
    } catch (err) {
      setError(err.message || "Failed to send reset email.");
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="auth-wrapper fade-in">
      <img src="/logo.png" alt="HI AWTO" className="auth-logo" />

      <h1 className="auth-title">Reset Password</h1>
      <p className="auth-subtitle">
        Enter your email and we&apos;ll send you a reset link.
      </p>

      {error && <div className="auth-error">{error}</div>}
      {status && (
        <div className="mb-3 rounded bg-green-50 text-green-700 px-3 py-2 text-sm">
          {status}
        </div>
      )}

      <form onSubmit={handleSubmit} className="auth-card">
        <input
          type="email"
          placeholder="Email"
          className="auth-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <button
          type="submit"
          disabled={sending}
          className="auth-button"
        >
          {sending ? "Sending reset link..." : "Send reset link"}
        </button>
      </form>

      <p className="auth-link">
        Remembered your password? <Link to="/login">Back to login</Link>
      </p>
    </div>
  );
}
