import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { getListingById } from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

export default function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  // Lightbox state
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        const data = await getListingById(id);
        setListing(data || null);
      } finally {
        setLoading(false);
      }
    })();
  }, [id]);

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading listing…</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Listing not found.</p>
        <Link to="/" className="text-blue-600 hover:underline mt-2 inline-block">
          ← Back to listings
        </Link>
      </div>
    );
  }

  const images =
    Array.isArray(listing.imageUrls) && listing.imageUrls.length
      ? listing.imageUrls
      : [FALLBACK_IMG];

  // Lightbox functions
  const openLightbox = (index) => {
    setLightboxIndex(index);
    setZoom(1);
    setLightboxOpen(true);
  };

  const closeLightbox = () => setLightboxOpen(false);
  const nextImage = () => {
    setLightboxIndex((i) => (i + 1) % images.length);
    setZoom(1);
  };
  const prevImage = () => {
    setLightboxIndex((i) => (i - 1 + images.length) % images.length);
    setZoom(1);
  };

  const zoomIn = () => setZoom((z) => Math.min(z + 0.25, 3));
  const zoomOut = () => setZoom((z) => Math.max(z - 0.25, 0.5));
  const resetZoom = () => setZoom(1);

  const activeImage = images[lightboxIndex] || images[0];

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <Link
        to="/"
        className="text-blue-600 text-sm hover:underline inline-block mb-4"
      >
        ← Back to listings
      </Link>

      <div className="grid lg:grid-cols-3 gap-10">
        {/* LEFT: IMAGES */}
        <div className="lg:col-span-2 space-y-4">
          {/* Main image */}
          <div
            className="h-72 md:h-96 bg-gray-200 rounded-xl overflow-hidden group cursor-zoom-in"
            onClick={() => openLightbox(lightboxIndex)}
          >
            <img
              src={activeImage}
              alt={listing.title || "Listing"}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform"
            />
          </div>

          {/* Thumbnails */}
          {images.length > 1 && (
            <div className="flex gap-2 overflow-x-auto pb-1">
              {images.map((img, idx) => (
                <button
                  key={idx}
                  type="button"
                  onClick={() => {
                    setLightboxIndex(idx);
                    openLightbox(idx);
                  }}
                  className={`h-16 w-24 rounded-lg overflow-hidden border ${
                    idx === lightboxIndex
                      ? "border-blue-500"
                      : "border-transparent opacity-70 hover:opacity-100"
                  }`}
                >
                  <img
                    src={img}
                    alt={`Thumbnail ${idx + 1}`}
                    className="w-full h-full object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT: DETAILS */}
        <div className="space-y-4">
          <div>
            <h1 className="text-2xl md:text-3xl font-bold text-slate-800">
              {listing.title || "Untitled Listing"}
            </h1>
            <p className="text-gray-600 mt-1">
              {listing.address || "No address provided"}
            </p>
          </div>

          <div className="bg-white rounded-xl shadow p-4 space-y-2">
            <p className="text-blue-600 font-semibold text-xl">
              ${listing.price?.toLocaleString?.() || listing.price} total
            </p>
            <p className="text-green-600 font-medium">
              ${listing.downPayment?.toLocaleString?.() || listing.downPayment} down
            </p>
            {listing.featured && (
              <p className="inline-flex mt-1 px-2 py-0.5 rounded-full bg-yellow-100 text-yellow-800 text-xs font-semibold">
                Featured Home
              </p>
            )}
          </div>

          {listing.description && (
            <div className="bg-white rounded-xl shadow p-4">
              <h2 className="font-semibold text-slate-800 mb-2">
                About this home
              </h2>
              <p className="text-gray-700 text-sm whitespace-pre-line">
                {listing.description}
              </p>
            </div>
          )}

          {/* CTA */}
          <div className="bg-blue-50 border border-blue-100 rounded-xl p-4">
            <h3 className="font-semibold text-slate-800 mb-1">
              Interested in this property?
            </h3>
            <p className="text-gray-600 text-sm mb-3">
              Log in or create an account to message the HI AWTO team.
            </p>
            <div className="flex gap-2">
              <Link
                to="/register"
                className="px-4 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700"
              >
                Get Started
              </Link>
              <Link
                to="/login"
                className="px-4 py-2 rounded-full border border-blue-200 text-blue-700 text-sm font-semibold hover:bg-blue-50"
              >
                Log In
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* ---------- LIGHTBOX ---------- */}
      {lightboxOpen && (
        <div
          className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center"
          onClick={closeLightbox}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Top bar */}
            <div className="flex items-center justify-between mb-3 text-white text-sm">
              <div>
                {listing.title || "Image"}{" "}
                <span className="opacity-70">
                  ({lightboxIndex + 1} / {images.length})
                </span>
              </div>
              <button
                onClick={closeLightbox}
                className="px-3 py-1 rounded-full border border-white/40 hover:bg-white/10"
              >
                Close ✕
              </button>
            </div>

            {/* Image */}
            <div className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]">
              <div className="overflow-auto flex items-center justify-center max-h-[80vh] max-w-full">
                <img
                  src={images[lightboxIndex]}
                  alt=""
                  className="max-h-[80vh] max-w-full transition-transform duration-150"
                  style={{ transform: `scale(${zoom})` }}
                />
              </div>
            </div>

            {/* Controls */}
            <div className="flex items-center justify-between mt-3 text-white text-sm">
              <div className="flex items-center gap-2">
                <button
                  onClick={prevImage}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ← Prev
                </button>
                <button
                  onClick={nextImage}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Next →
                </button>
              </div>

              <div className="flex items-center gap-2">
                <button
                  onClick={zoomOut}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  −
                </button>
                <span className="text-xs opacity-80">{(zoom * 100).toFixed(0)}%</span>
                <button
                  onClick={zoomIn}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  +
                </button>
                <button
                  onClick={resetZoom}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
