// src/pages/ListingDetails.jsx
import React, {
  useEffect,
  useState,
  useCallback,
  useRef,
} from "react";
import { useParams, Link } from "react-router-dom";
import { getListingById } from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

export default function ListingDetails() {
  const { id } = useParams();

  // -------------------------
  // STATE
  // -------------------------
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  // Touch + gesture refs
  const touchStartXRef = useRef(null);
  const touchEndXRef = useRef(null);
  const lastTapRef = useRef(0);

  const SWIPE_THRESHOLD = 50;
  const DOUBLE_TAP_THRESHOLD = 250;

  // -------------------------
  // LOAD LISTING
  // -------------------------
  useEffect(() => {
    (async () => {
      const data = await getListingById(id);
      setListing(data || null);
      setLoading(false);
    })();
  }, [id]);

  // -------------------------
  // LIGHTBOX KEYBOARD NAV
  // (MUST BE ABOVE EARLY RETURNS)
  // -------------------------
  const handleKeyDown = useCallback(
    (e) => {
      if (!lightboxOpen) return;

      if (e.key === "ArrowRight") nextImage();
      if (e.key === "ArrowLeft") prevImage();
      if (e.key === "Escape") closeLightbox();
    },
    [lightboxOpen]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // -------------------------
  // SHARE
  // -------------------------
  const handleUniversalShare = async (url) => {
    try {
      const blob = await fetch(url).then((r) => r.blob());
      const file = new File([blob], "hi-awto-share.jpg", { type: blob.type });

      if (navigator.canShare && navigator.canShare({ files: [file] })) {
        await navigator.share({ title: "Share Image", files: [file] });
        return;
      }

      if (navigator.share) {
        await navigator.share({ title: "Share Image", url });
        return;
      }

      await navigator.clipboard.writeText(url);
      alert("Link copied. This device does not support direct sharing.");
    } catch (err) {
      console.error(err);
    }
  };

  // -------------------------
  // SAVE TO PHOTOS
  // -------------------------
  const handleSaveToPhotos = async (url) => {
    try {
      const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

      if (isMobile && navigator.share) {
        const blob = await fetch(url).then((r) => r.blob());
        const file = new File([blob], "hi-awto-image.jpg", { type: blob.type });

        await navigator.share({ title: "Save Image", files: [file] });
        return;
      }

      const blob = await fetch(url).then((r) => r.blob());
      const blobUrl = URL.createObjectURL(blob);

      const a = document.createElement("a");
      a.href = blobUrl;
      a.download = "hi-awto-image.jpg";
      a.click();

      URL.revokeObjectURL(blobUrl);
    } catch (err) {
      console.error(err);
    }
  };

  // -------------------------
  // COPY IMAGE
  // -------------------------
  const handleCopyImage = async (url) => {
    try {
      if (window.ClipboardItem && navigator.clipboard?.write) {
        const blob = await fetch(url).then((r) => r.blob());
        const item = new ClipboardItem({ [blob.type]: blob });
        await navigator.clipboard.write([item]);
        alert("Image copied.");
        return;
      }

      await navigator.clipboard.writeText(url);
      alert("Image link copied.");
    } catch (err) {
      console.error(err);
    }
  };

  // -------------------------
  // LIGHTBOX FUNCTIONS
  // -------------------------
  const openLightbox = (i) => {
    setLightboxIndex(i);
    setZoom(1);
    setLightboxOpen(true);
  };

  const closeLightbox = () => setLightboxOpen(false);

  const nextImage = () => {
    setLightboxIndex((i) => (i + 1) % images.length);
    setZoom(1);
  };

  const prevImage = () => {
    setLightboxIndex((i) => (i - 1 + images.length) % images.length);
    setZoom(1);
  };

  const zoomIn = () => setZoom((z) => Math.min(3, z + 0.25));
  const zoomOut = () => setZoom((z) => Math.max(0.5, z - 0.25));
  const resetZoom = () => setZoom(1);

  const handleDoubleTap = () => {
    const now = Date.now();
    if (now - lastTapRef.current < DOUBLE_TAP_THRESHOLD) {
      setZoom((z) => (z === 1 ? 2 : 1));
    }
    lastTapRef.current = now;
  };

  const handleTouchStart = (e) => {
    const x = e.touches[0].clientX;
    touchStartXRef.current = x;
    touchEndXRef.current = x;
  };

  const handleTouchMove = (e) => {
    touchEndXRef.current = e.touches[0].clientX;
  };

  const handleTouchEnd = () => {
    const start = touchStartXRef.current;
    const end = touchEndXRef.current;
    const diff = end - start;

    if (Math.abs(diff) > SWIPE_THRESHOLD) {
      if (diff < 0) nextImage();
      else prevImage();
    }
  };

  // -------------------------
  // EARLY RETURNS (SAFE NOW)
  // -------------------------
  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading‚Ä¶</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Listing not found.</p>
        <Link to="/" className="text-blue-600 hover:underline">‚Üê Back</Link>
      </div>
    );
  }

  const images = listing.imageUrls?.length ? listing.imageUrls : [FALLBACK_IMG];
  const activeImage = images[lightboxIndex];

  // -------------------------
  // RENDER
  // -------------------------
  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <Link to="/" className="text-blue-600 hover:underline">‚Üê Back</Link>

      <div className="grid lg:grid-cols-3 gap-10 mt-4">
        <div className="lg:col-span-2 space-y-4">
          <div
            className="h-72 md:h-96 rounded-xl overflow-hidden cursor-zoom-in"
            onClick={() => openLightbox(lightboxIndex)}
          >
            <img src={activeImage} className="w-full h-full object-cover" />
          </div>

          <div className="flex gap-2 overflow-x-auto">
            {images.map((img, idx) => (
              <button
                key={idx}
                onClick={() => openLightbox(idx)}
                className={`h-16 w-24 rounded-lg overflow-hidden border ${
                  idx === lightboxIndex
                    ? "border-blue-500"
                    : "border-transparent opacity-70 hover:opacity-100"
                }`}
              >
                <img src={img} className="w-full h-full object-cover" />
              </button>
            ))}
          </div>
        </div>

        <div className="space-y-4">
          <h1 className="text-3xl font-bold">{listing.title}</h1>
          <p className="text-gray-600">{listing.address}</p>

          <div className="bg-white shadow rounded-xl p-4">
            <p className="text-2xl font-semibold text-blue-600">
              ${listing.price?.toLocaleString()}
            </p>
            <p className="text-green-600">
              ${listing.downPayment?.toLocaleString()} down
            </p>
          </div>

          {listing.description && (
            <div className="bg-white shadow rounded-xl p-4 text-sm text-gray-700">
              {listing.description}
            </div>
          )}
        </div>
      </div>

      {/* LIGHTBOX */}
      {lightboxOpen && (
        <div
          className="fixed inset-0 bg-black/80 flex items-center justify-center z-50"
          onClick={closeLightbox}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* TOP */}
            <div className="flex justify-between mb-3 text-white text-sm">
              <div>{lightboxIndex + 1}/{images.length}</div>

              <button className="px-3 py-1 border border-white/40 rounded-full" onClick={closeLightbox}>
                Close ‚úï
              </button>
            </div>

            {/* IMAGE */}
            <div
              className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]"
              onTouchStart={handleTouchStart}
              onTouchMove={handleTouchMove}
              onTouchEnd={(e) => {
                handleTouchEnd();
                handleDoubleTap();
              }}
              onDoubleClick={handleDoubleTap}
            >
              <img
                src={activeImage}
                className="max-h-[80vh] max-w-full transition-transform"
                style={{ transform: `scale(${zoom})` }}
              />
            </div>

            {/* CONTROLS */}
            <div className="flex justify-between items-center mt-3 text-white text-sm">
              
              {/* Prev / Next */}
              <div className="flex gap-2">
                <button onClick={prevImage} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">
                  ‚Üê Prev
                </button>
                <button onClick={nextImage} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">
                  Next ‚Üí
                </button>
              </div>

              {/* DOWNLOAD / SAVE / COPY / SHARE */}
              <div className="flex gap-2">
                <a
                  href={activeImage}
                  download={`hi-awto-image-${lightboxIndex + 1}.jpg`}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ‚¨á Download
                </a>

                <button onClick={() => handleSaveToPhotos(activeImage)} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">
                  üì± Save
                </button>

                <button onClick={() => handleCopyImage(activeImage)} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">
                  üìã Copy
                </button>

                <button onClick={() => handleUniversalShare(activeImage)} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">
                  üîó Share
                </button>
              </div>

              {/* ZOOM */}
              <div className="flex items-center gap-2">
                <button onClick={zoomOut} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">‚àí</button>
                <span className="opacity-70">{(zoom * 100).toFixed(0)}%</span>
                <button onClick={zoomIn} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">+</button>
                <button onClick={resetZoom} className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10">Reset</button>
              </div>

            </div>
          </div>
        </div>
      )}
    </div>
  );
}
