// src/pages/MyListings.jsx
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";
import {
  getListingsForUser,
  deleteListing,
} from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

export default function MyListings() {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) return;

    (async () => {
      setLoading(true);
      setError("");
      try {
        const data = await getListingsForUser(user.uid);
        setListings(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error(err);
        setError("Failed to load your listings.");
      } finally {
        setLoading(false);
      }
    })();
  }, [user]);

  const handleDelete = async (id) => {
    const ok = window.confirm(
      "Are you sure you want to permanently delete this listing?"
    );
    if (!ok) return;

    try {
      await deleteListing(id);
      setListings((prev) => prev.filter((l) => l.id !== id));
    } catch (err) {
      console.error(err);
      alert("Failed to delete listing. Please try again.");
    }
  };

  const handleEdit = (id) => {
    navigate(`/edit-listing/${id}`);
  };

  if (!user) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-600">
          Please log in to view and manage your listings.
        </p>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-3 mb-6">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-slate-800">
            My Listings
          </h1>
          <p className="text-gray-500 text-sm mt-1">
            Manage your lease-to-own properties, update details, or remove
            listings.
          </p>
        </div>
        <Link
          to="/create-listing"
          className="inline-flex items-center justify-center px-5 py-2 rounded-full bg-blue-600 text-white text-sm font-semibold hover:bg-blue-700"
        >
          + New Listing
        </Link>
      </div>

      {loading && (
        <p className="text-gray-500 text-sm">Loading your listings…</p>
      )}

      {error && (
        <p className="text-red-600 text-sm mb-4">
          {error}
        </p>
      )}

      {!loading && listings.length === 0 && (
        <div className="bg-white rounded-xl p-6 shadow text-gray-600 text-sm">
          You don&apos;t have any listings yet. Click{" "}
          <span className="font-semibold">“New Listing”</span> to create one.
        </div>
      )}

      {!loading && listings.length > 0 && (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {listings.map((listing) => {
            const mainImage =
              (listing.imageUrls && listing.imageUrls[0]) || FALLBACK_IMG;

            return (
              <div
                key={listing.id}
                className="bg-white rounded-xl shadow hover:shadow-lg transition overflow-hidden flex flex-col"
              >
                {/* Image */}
                <Link
                  to={`/listing/${listing.id}`}
                  className="block h-40 bg-gray-200 overflow-hidden relative"
                >
                  <img
                    src={mainImage}
                    alt={listing.title || "Listing"}
                    className="w-full h-full object-cover hover:scale-105 transition-transform"
                  />
                  {listing.featured && (
                    <span className="absolute top-2 left-2 px-2 py-0.5 rounded-full text-xs font-semibold bg-yellow-400 text-slate-900 shadow">
                      Featured
                    </span>
                  )}
                </Link>

                {/* Content */}
                <div className="flex-1 flex flex-col p-4">
                  <div className="flex-1">
                    <Link to={`/listing/${listing.id}`}>
                      <h2 className="font-semibold text-slate-800 text-lg line-clamp-1">
                        {listing.title || "Untitled listing"}
                      </h2>
                    </Link>
                    <p className="text-gray-600 text-sm mt-1 line-clamp-2">
                      {listing.address || "No address provided"}
                    </p>

                    <div className="mt-3">
                      <p className="text-blue-600 font-semibold text-sm">
                        ${listing.price?.toLocaleString?.() || listing.price}{" "}
                        <span className="text-gray-500 font-normal">
                          total
                        </span>
                      </p>
                      <p className="text-green-600 text-xs mt-0.5">
                        $
                        {listing.downPayment?.toLocaleString?.() ||
                          listing.downPayment}{" "}
                        down
                      </p>
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="flex justify-between items-center mt-4 pt-3 border-t border-gray-100">
                    <Link
                      to={`/listing/${listing.id}`}
                      className="text-blue-600 text-xs font-semibold hover:underline"
                    >
                      View
                    </Link>

                    <div className="flex gap-2">
                      <button
                        type="button"
                        onClick={() => handleEdit(listing.id)}
                        className="px-2 py-1 rounded-full border border-blue-200 text-blue-700 text-xs font-semibold hover:bg-blue-50"
                      >
                        Edit
                      </button>
                      <button
                        type="button"
                        onClick={() => handleDelete(listing.id)}
                        className="px-2 py-1 rounded-full border border-red-200 text-red-600 text-xs font-semibold hover:bg-red-50"
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
