import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllListings } from "../firebase/listings.js";

export default function Home() {
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const data = await getAllListings();
        setListings(data);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <div className="w-full min-h-screen bg-gray-50 pb-24">

      {/* HERO SECTION */}
      <div className="flex flex-col items-center text-center mt-12 mb-10 px-4">
        
        {/* Optional Hero Logo */}
        <img 
          src="/logo.png" 
          alt="HI AWTO Logo" 
          className="h-24 w-auto drop-shadow-sm mb-4"
        />

        <h1 className="text-2xl md:text-3xl font-bold text-blue-600 tracking-wide">
          HERE IS ANOTHER WAY TO OWN
        </h1>

        <p className="text-lg mt-4 text-gray-600">
          Available Lease-to-Own Listings
        </p>
      </div>

      {/* MAIN CONTENT */}
      <div className="max-w-6xl mx-auto px-4">

        {/* LOADING STATE */}
        {loading && (
          <div className="text-center text-gray-500 text-lg mt-20">
            Loading listingsâ€¦
          </div>
        )}

        {/* EMPTY STATE */}
        {!loading && listings.length === 0 && (
          <div className="text-center text-gray-500 text-lg mt-20">
            No listings yet. Check back soon.
          </div>
        )}

        {/* LISTINGS GRID */}
        {!loading && listings.length > 0 && (
          <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-10">
            {listings.map((listing) => (
              <Link
                key={listing.id}
                to={`/listing/${listing.id}`}
                className="bg-white rounded-xl shadow-sm border hover:shadow-md transition p-5"
              >
                <h3 className="font-semibold text-lg text-slate-800">
                  {listing.title || "Untitled Listing"}
                </h3>

                <p className="text-gray-600 text-sm">
                  {listing.address || "No address provided"}
                </p>

                <p className="text-blue-600 font-semibold mt-2">
                  ${listing.price?.toLocaleString?.() || listing.price} total
                </p>
                <p className="text-green-600 font-medium text-sm">
                  ${listing.downPayment?.toLocaleString?.() || listing.downPayment} down
                </p>
              </Link>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
