import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function Navbar() {
  const { user, role } = useAuth();
  const navigate = useNavigate();

  const isAdmin = role === "admin";

  const handleLogout = async () => {
    // You can call logout() from auth.js here if you want
    const { logout } = await import("../firebase/auth.js");
    await logout();
    navigate("/login");
  };

  return (
    <nav className="flex items-center justify-between px-4 py-3 border-b bg-white">
      <Link to="/" className="font-bold text-xl">
        HI-AWTO
      </Link>

      <div className="flex gap-4 items-center">
        {/* Always visible */}
        <Link to="/" className="hover:text-blue-600">
          Home
        </Link>

        {/* Authenticated user links */}
        {user && (
          <>
            <Link to="/my-listings" className="hover:text-blue-600">
              My Listings
            </Link>
            <Link to="/messages" className="hover:text-blue-600">
              Messages
            </Link>
            <Link to="/profile" className="hover:text-blue-600">
              Profile
            </Link>
          </>
        )}

        {/* Admin */}
        {user && isAdmin && (
          <Link to="/admin/messages" className="hover:text-blue-600">
            Admin
          </Link>
        )}

        {/* Login / Register vs Logout */}
        {!user && (
          <>
            <Link to="/login" className="hover:text-blue-600">
              Login
            </Link>
            <Link to="/register" className="hover:text-blue-600">
              Register
            </Link>
          </>
        )}

        {user && (
          <button
            onClick={handleLogout}
            className="text-red-600 font-medium hover:opacity-80"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}
