import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function Navbar() {
  const { user, role } = useAuth();
  const navigate = useNavigate();

  const isAdmin = role === "admin";

  const handleLogout = async () => {
    const { logout } = await import("../firebase/auth.js");
    await logout();
    navigate("/login");
  };

  return (
    <nav className="flex items-center justify-between px-4 py-3 border-b border-neutral-800 bg-black">
      {/* Logo */}
      <Link to="/" className="flex items-center gap-2">
        <img
          src="/logo.png"
          alt="HI AWTO Logo"
          className="h-12 w-auto"
        />
      </Link>

      {/* Links */}
      <div className="flex gap-4 items-center text-white text-sm md:text-base">
        <Link to="/" className="hover:text-[#4EC3FF]">
          Home
        </Link>

        {user && (
          <>
            <Link to="/my-listings" className="hover:text-[#4EC3FF]">
              My Listings
            </Link>
            <Link to="/messages" className="hover:text-[#4EC3FF]">
              Messages
            </Link>
            <Link to="/profile" className="hover:text-[#4EC3FF]">
              Profile
            </Link>
          </>
        )}

        {user && isAdmin && (
          <Link to="/admin/messages" className="hover:text-[#A8FF2A]">
            Admin
          </Link>
        )}

        {!user && (
          <>
            <Link to="/login" className="hover:text-[#4EC3FF]">
              Login
            </Link>
            <Link to="/register" className="hover:text-[#4EC3FF]">
              Register
            </Link>
          </>
        )}

        {user && (
          <button
            onClick={handleLogout}
            className="text-red-500 font-medium hover:opacity-80"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}
