// src/pages/ListingDetails.jsx
import React, { useEffect, useState, useCallback, useRef } from "react";
import { useParams, Link } from "react-router-dom";
import { getListingById } from "../firebase/listings.js";

const FALLBACK_IMG = "/placeholder-listing.jpg";

export default function ListingDetails() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [loading, setLoading] = useState(true);

  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxIndex, setLightboxIndex] = useState(0);
  const [zoom, setZoom] = useState(1);

  // Touch swipe refs
  const touchStartXRef = useRef(null);
  const touchEndXRef = useRef(null);
  const SWIPE_THRESHOLD = 50; // px

  useEffect(() => {
    (async () => {
      setLoading(true);
      try {
        const data = await getListingById(id);
        setListing(data || null);
      } finally {
        setLoading(false);
      }
    })();
  }, [id]);

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Loading listing‚Ä¶</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-10">
        <p className="text-gray-500">Listing not found.</p>
        <Link
          to="/"
          className="text-blue-600 hover:underline mt-2 inline-block"
        >
          ‚Üê Back
        </Link>
      </div>
    );
  }

  const images =
    Array.isArray(listing.imageUrls) && listing.imageUrls.length
      ? listing.imageUrls
      : [FALLBACK_IMG];

  const openLightbox = (index) => {
    setLightboxIndex(index);
    setZoom(1);
    setLightboxOpen(true);
  };

  const closeLightbox = () => setLightboxOpen(false);

  const nextImage = () => {
    setLightboxIndex((i) => (i + 1) % images.length);
    setZoom(1);
  };

  const prevImage = () => {
    setLightboxIndex((i) => (i - 1 + images.length) % images.length);
    setZoom(1);
  };

  const zoomIn = () => setZoom((z) => Math.min(z + 0.25, 3));
  const zoomOut = () => setZoom((z) => Math.max(z - 0.25, 0.5));
  const resetZoom = () => setZoom(1);

  // üî• Keyboard controls: Esc, ‚Üê, ‚Üí
  const handleKeyDown = useCallback(
    (e) => {
      if (!lightboxOpen) return;

      if (e.key === "Escape") {
        closeLightbox();
      } else if (e.key === "ArrowRight") {
        nextImage();
      } else if (e.key === "ArrowLeft") {
        prevImage();
      }
    },
    [lightboxOpen, images.length]
  );

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [handleKeyDown]);

  // üî• Touch swipe handlers
  const handleTouchStart = (e) => {
    if (!lightboxOpen) return;
    const touch = e.touches[0];
    touchStartXRef.current = touch.clientX;
    touchEndXRef.current = touch.clientX;
  };

  const handleTouchMove = (e) => {
    if (!lightboxOpen) return;
    const touch = e.touches[0];
    touchEndXRef.current = touch.clientX;
  };

  const handleTouchEnd = () => {
    if (!lightboxOpen) return;
    const startX = touchStartXRef.current;
    const endX = touchEndXRef.current;
    if (startX == null || endX == null) return;

    const deltaX = endX - startX;
    if (Math.abs(deltaX) < SWIPE_THRESHOLD) return;

    if (deltaX < 0) {
      // swipe left ‚Üí next
      nextImage();
    } else {
      // swipe right ‚Üí prev
      prevImage();
    }

    touchStartXRef.current = null;
    touchEndXRef.current = null;
  };

  const activeImage = images[lightboxIndex] || images[0];

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <Link
        to="/"
        className="text-blue-600 text-sm hover:underline inline-block mb-4"
      >
        ‚Üê Back
      </Link>

      <div className="grid lg:grid-cols-3 gap-10">
        {/* LEFT: IMAGES */}
        <div className="lg:col-span-2 space-y-4">
          <div
            className="h-72 md:h-96 bg-gray-200 rounded-xl overflow-hidden cursor-zoom-in"
            onClick={() => openLightbox(lightboxIndex)}
          >
            <img
              src={activeImage}
              alt={listing.title || "Listing"}
              className="w-full h-full object-cover"
            />
          </div>

          {images.length > 1 && (
            <div className="flex gap-2 overflow-x-auto">
              {images.map((img, idx) => (
                <button
                  key={idx}
                  type="button"
                  onClick={() => openLightbox(idx)}
                  className={`h-16 w-24 rounded-lg overflow-hidden border ${
                    idx === lightboxIndex
                      ? "border-blue-500"
                      : "border-transparent opacity-70 hover:opacity-100"
                  }`}
                >
                  <img
                    src={img}
                    alt={`Thumbnail ${idx + 1}`}
                    className="w-full h-full object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* RIGHT: DETAILS */}
        <div className="space-y-4">
          <h1 className="text-3xl font-bold text-slate-800">
            {listing.title || "Untitled Listing"}
          </h1>
          <p className="text-gray-600">
            {listing.address || "No address provided"}
          </p>

          <div className="bg-white rounded-xl shadow p-4 space-y-2">
            <p className="text-blue-600 font-semibold text-2xl">
              ${listing.price?.toLocaleString?.() || listing.price}
            </p>
            <p className="text-green-600 font-medium">
              ${listing.downPayment?.toLocaleString?.() || listing.downPayment}{" "}
              down
            </p>
          </div>

          {listing.description && (
            <div className="bg-white rounded-xl shadow p-4">
              <h2 className="font-semibold mb-2 text-slate-800">
                About this home
              </h2>
              <p className="text-gray-700 whitespace-pre-line text-sm">
                {listing.description}
              </p>
            </div>
          )}
        </div>
      </div>

      {/* LIGHTBOX */}
      {lightboxOpen && (
        <div
          className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center"
          onClick={closeLightbox}
        >
          <div
            className="relative max-w-5xl max-h-[90vh] w-full px-4"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Top bar */}
            <div className="flex items-center justify-between mb-3 text-white text-sm">
              <div>
                Image {lightboxIndex + 1} / {images.length}
              </div>
              <button
                onClick={closeLightbox}
                className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
              >
                Close ‚úï
              </button>
            </div>

            {/* Image with swipe support */}
            <div
              className="bg-black rounded-lg overflow-hidden flex items-center justify-center max-h-[80vh]"
              onTouchStart={handleTouchStart}
              onTouchMove={handleTouchMove}
              onTouchEnd={handleTouchEnd}
            >
              <img
                src={activeImage}
                alt=""
                className="max-h-[80vh] max-w-full transition-transform"
                style={{ transform: `scale(${zoom})` }}
              />
            </div>

            {/* Controls */}
            <div className="flex items-center justify-between mt-3 text-white text-sm">
              <div className="flex gap-2">
                <button
                  onClick={prevImage}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ‚Üê Prev
                </button>
                <button
                  onClick={nextImage}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Next ‚Üí
                </button>
              </div>

              <div className="flex gap-2 items-center">
                <button
                  onClick={zoomOut}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  ‚àí
                </button>
                <span className="text-xs opacity-80">
                  {(zoom * 100).toFixed(0)}%
                </span>
                <button
                  onClick={zoomIn}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  +
                </button>
                <button
                  onClick={resetZoom}
                  className="px-3 py-1 border border-white/40 rounded-full hover:bg-white/10"
                >
                  Reset
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
