// src/firebase/auth.js
import { initializeApp } from "firebase/app";
import {
  getAuth,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signOut,
  sendPasswordResetEmail,
  GoogleAuthProvider,
  OAuthProvider,
  signInWithPopup,
  updateProfile,
} from "firebase/auth";
import { firebaseConfig } from "./config";

// Initialize Firebase app (if not already initialized elsewhere)
const app = initializeApp(firebaseConfig);

// Auth instance
export const auth = getAuth(app);

// -----------------------
// Core Auth Functions
// -----------------------

export async function loginUser({ email, password }) {
  return signInWithEmailAndPassword(auth, email, password);
}

export async function registerUser({ email, password, displayName }) {
  const cred = await createUserWithEmailAndPassword(auth, email, password);

  // Set displayName on Firebase Auth user
  if (displayName) {
    await updateProfile(cred.user, { displayName });
  }

  return cred;
}

export async function logout() {
  return signOut(auth);
}

// -----------------------
// Forgot Password
// -----------------------

export async function resetPassword(email) {
  if (!email) throw new Error("Please provide an email address.");
  return sendPasswordResetEmail(auth, email);
}

// -----------------------
// Social Login
// -----------------------

export async function loginWithGoogle() {
  const provider = new GoogleAuthProvider();
  provider.setCustomParameters({ prompt: "select_account" });
  return signInWithPopup(auth, provider);
}

export async function loginWithApple() {
  // NOTE: Apple provider must be enabled in Firebase Console.
  const provider = new OAuthProvider("apple.com");
  return signInWithPopup(auth, provider);
}
