import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllListings } from "../firebase/listings.js";

export default function Home() {
  const [listings, setListings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const data = await getAllListings();
        setListings(data);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Lease-to-Own Listings</h1>

      {loading && <div>Loading listings...</div>}

      {!loading && listings.length === 0 && (
        <div>No listings yet. Check back soon.</div>
      )}

      <div className="grid gap-4 md:grid-cols-2">
        {listings.map((l) => (
          <Link
            key={l.id}
            to={`/listing/${l.id}`}
            className="border rounded p-3 hover:shadow"
          >
            <h2 className="font-semibold">{l.title || "Untitled listing"}</h2>
            <p className="text-sm text-gray-600">{l.address}</p>
            <p className="mt-1">
              ${l.price?.toLocaleString?.() || l.price} total â€¢ $
              {l.downPayment?.toLocaleString?.() || l.downPayment} down
            </p>
          </Link>
        ))}
      </div>
    </div>
  );
}
