import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../firebase/AuthContext.jsx";

export default function Navbar() {
  const { user, role } = useAuth();
  const navigate = useNavigate();

  const [menuOpen, setMenuOpen] = useState(false);
  const [avatarMenu, setAvatarMenu] = useState(false);
  const [shrink, setShrink] = useState(false);

  const avatarRef = useRef(null);
  const avatarMenuRef = useRef(null);

  const isAdmin = role === "admin";

  const handleLogout = async () => {
    const { logout } = await import("../firebase/auth.js");
    await logout();
    navigate("/login");
  };

  // Shrinking navbar effect
  useEffect(() => {
    const handleScroll = () => {
      setShrink(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Close avatar menu when clicking outside
  useEffect(() => {
    function handleClickOutside(e) {
      if (
        avatarMenuRef.current &&
        !avatarMenuRef.current.contains(e.target) &&
        !avatarRef.current.contains(e.target)
      ) {
        setAvatarMenu(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Create initials fallback
  const initials = user?.displayName
    ? user.displayName
        .split(" ")
        .map((n) => n[0])
        .join("")
        .slice(0, 2)
        .toUpperCase()
    : user?.email?.[0]?.toUpperCase();

  return (
    <>
      {/* NAVBAR */}
      <nav
        className={`
          fixed top-0 left-0 w-full z-50 backdrop-blur bg-white/80 border-b border-gray-200 
          transition-all duration-300 
          ${shrink ? "py-2 shadow-sm" : "py-4"}
        `}
      >
        <div className="max-w-7xl mx-auto px-4 flex items-center justify-between">

          {/* LOGO */}
          <Link to="/" className="flex items-center gap-2">
            <img
              src="/logo-nav.png"
              alt="HI AWTO"
              className={`transition-all duration-300 ${shrink ? "h-6" : "h-8"}`}
            />
          </Link>

          {/* DESKTOP MENU */}
          <div className="hidden md:flex items-center gap-8 text-slate-800 font-medium">

            <Link to="/" className="hover:text-blue-600">Home</Link>
            <Link to="/how-it-works" className="hover:text-blue-600">How It Works</Link>

            {!user && (
              <>
                <Link to="/login" className="hover:text-blue-600">Login</Link>
                <Link to="/register" className="hover:text-blue-600">Register</Link>
              </>
            )}

            {user && (
              <>
                <Link to="/my-listings" className="hover:text-blue-600">My Listings</Link>
                <Link to="/messages" className="hover:text-blue-600">Messages</Link>
              </>
            )}

            {/* Avatar */}
            {user && (
              <div className="relative">
                <button
                  ref={avatarRef}
                  onClick={() => setAvatarMenu((v) => !v)}
                  className="h-9 w-9 rounded-full bg-gray-200 overflow-hidden border border-gray-300 shadow-sm hover:shadow-md transition"
                >
                  {user.photoURL ? (
                    <img
                      src={user.photoURL}
                      alt="avatar"
                      className="h-full w-full object-cover"
                    />
                  ) : (
                    <div className="h-full w-full flex items-center justify-center text-xs font-bold text-slate-700">
                      {initials}
                    </div>
                  )}
                </button>

                {/* Dropdown menu */}
                {avatarMenu && (
                  <div
                    ref={avatarMenuRef}
                    className="absolute right-0 mt-2 w-48 bg-white rounded-xl shadow-lg border border-gray-200 py-2 text-sm z-50 animate-fadeIn"
                  >
                    <Link
                      to="/profile"
                      className="block px-4 py-2 hover:bg-slate-50"
                      onClick={() => setAvatarMenu(false)}
                    >
                      Profile
                    </Link>

                    <Link
                      to="/my-listings"
                      className="block px-4 py-2 hover:bg-slate-50"
                      onClick={() => setAvatarMenu(false)}
                    >
                      My Listings
                    </Link>

                    <Link
                      to="/messages"
                      className="block px-4 py-2 hover:bg-slate-50"
                      onClick={() => setAvatarMenu(false)}
                    >
                      Messages
                    </Link>

                    {isAdmin && (
                      <Link
                        to="/admin/messages"
                        className="block px-4 py-2 hover:bg-slate-50 text-green-600"
                        onClick={() => setAvatarMenu(false)}
                      >
                        Admin
                      </Link>
                    )}

                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 text-red-600 hover:bg-red-50"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* MOBILE MENU BUTTON */}
          <button
            className="md:hidden text-slate-800 text-3xl"
            onClick={() => setMenuOpen(true)}
          >
            ☰
          </button>
        </div>
      </nav>

      {/* MOBILE OVERLAY */}
      {menuOpen && (
        <div
          className="fixed inset-0 bg-black/40 backdrop-blur-sm z-40"
          onClick={() => setMenuOpen(false)}
        />
      )}

      {/* MOBILE SLIDE DOWN MENU */}
      <div
        className={`md:hidden fixed top-0 left-0 w-full bg-white z-50 shadow-lg transform transition-transform duration-300 ${
          menuOpen ? "translate-y-0" : "-translate-y-full"
        }`}
      >
        <div className="flex items-center justify-between px-4 py-4 border-b border-gray-200">
          <img src="/logo-nav.png" alt="HI AWTO" className="h-8" />

          <button
            className="text-3xl text-slate-700"
            onClick={() => setMenuOpen(false)}
          >
            ✕
          </button>
        </div>

        <div className="flex flex-col px-6 py-4 text-lg text-slate-800 space-y-4">

          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/how-it-works" onClick={() => setMenuOpen(false)}>How It Works</Link>

          {!user && (
            <>
              <Link to="/login" onClick={() => setMenuOpen(false)}>Login</Link>
              <Link to="/register" onClick={() => setMenuOpen(false)}>Register</Link>
            </>
          )}

          {user && (
            <>
              {/* MOBILE AVATAR HEADER */}
              <div className="flex items-center gap-3 pt-2 pb-2 border-b border-gray-200">
                <div className="h-12 w-12 rounded-full bg-gray-200 overflow-hidden border border-gray-300">
                  {user.photoURL ? (
                    <img
                      src={user.photoURL}
                      alt="avatar"
                      className="h-full w-full object-cover"
                    />
                  ) : (
                    <div className="h-full w-full flex items-center justify-center font-semibold text-slate-700">
                      {initials}
                    </div>
                  )}
                </div>
                <div>
                  <p className="font-semibold">{user.displayName || "User"}</p>
                  <p className="text-sm text-slate-500">{user.email}</p>
                </div>
              </div>

              <Link to="/profile" onClick={() => setMenuOpen(false)}>Profile</Link>
              <Link to="/my-listings" onClick={() => setMenuOpen(false)}>My Listings</Link>
              <Link to="/messages" onClick={() => setMenuOpen(false)}>Messages</Link>

              {isAdmin && (
                <Link
                  to="/admin/messages"
                  className="text-green-600"
                  onClick={() => setMenuOpen(false)}
                >
                  Admin
                </Link>
              )}

              <button
                onClick={() => {
                  handleLogout();
                  setMenuOpen(false);
                }}
                className="text-red-600 text-left pt-2"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </>
  );
}
